import{_ as o,c as i,o as t,ag as r}from"./chunks/framework.B0we9iV-.js";const _=JSON.parse('{"title":"01. 项目愿景 (Vision)","description":"","frontmatter":{},"headers":[],"relativePath":"white-book/00-Manifesto/01-Vision.md","filePath":"white-book/00-Manifesto/01-Vision.md"}'),a={name:"white-book/00-Manifesto/01-Vision.md"};function n(l,e,s,p,c,d){return t(),i("div",null,[...e[0]||(e[0]=[r('<h1 id="_01-项目愿景-vision" tabindex="-1">01. 项目愿景 (Vision) <a class="header-anchor" href="#_01-项目愿景-vision" aria-label="Permalink to &quot;01. 项目愿景 (Vision)&quot;">​</a></h1><h2 id="webos-for-crypto" tabindex="-1">WebOS for Crypto <a class="header-anchor" href="#webos-for-crypto" aria-label="Permalink to &quot;WebOS for Crypto&quot;">​</a></h2><p>KeyApp 不仅仅是一个多链钱包，它是一个 <strong>去中心化 DApp 运行时 (Decentralized DApp Runtime)</strong>，即 <strong>WebOS</strong>。</p><h3 id="核心理念" tabindex="-1">核心理念 <a class="header-anchor" href="#核心理念" aria-label="Permalink to &quot;核心理念&quot;">​</a></h3><ol><li><p><strong>微内核 (Micro-Kernel)</strong>:</p><ul><li>通过 <code>miniapp-runtime</code> 实现应用进程隔离。</li><li>每个 DApp 运行在独立的 iframe 沙箱中。</li><li>通过 <code>BioBridge</code> 进行受控的跨进程通信。</li></ul></li><li><p><strong>驱动分离 (Driver Abstraction)</strong>:</p><ul><li>底层链的差异被封装在 <code>ChainAdapter</code> 层。</li><li>上层应用通过统一的 <code>ApiProvider</code> 接口与链交互。</li><li>支持动态加载新的链驱动。</li></ul></li><li><p><strong>沉浸式体验 (Immersive Shell)</strong>:</p><ul><li>提供类似原生 OS 的多任务管理 (Stack View)。</li><li>支持手势导航、FLIP 动画和系统级通知。</li></ul></li></ol><h3 id="为什么这样做" tabindex="-1">为什么这样做？ <a class="header-anchor" href="#为什么这样做" aria-label="Permalink to &quot;为什么这样做？&quot;">​</a></h3><p>传统的 DApp 浏览器只是一个 WebView。KeyApp 试图在浏览器中重建操作系统的核心能力：<strong>安全隔离</strong>、<strong>资源调度</strong> 和 <strong>统一交互</strong>。这使得 KeyApp 能够成为连接 Web2 用户与 Web3 世界的终极网关。</p>',7)])])}const u=o(a,[["render",n]]);export{_ as __pageData,u as default};
