import{_ as e,c as a,o as r,ag as o}from"./chunks/framework.B0we9iV-.js";const g=JSON.parse('{"title":"02. 技术栈清单 (Tech Stack)","description":"","frontmatter":{},"headers":[],"relativePath":"white-book/00-Manifesto/02-Tech-Stack.md","filePath":"white-book/00-Manifesto/02-Tech-Stack.md"}'),i={name:"white-book/00-Manifesto/02-Tech-Stack.md"};function n(l,t,s,c,h,d){return r(),a("div",null,[...t[0]||(t[0]=[o('<h1 id="_02-技术栈清单-tech-stack" tabindex="-1">02. 技术栈清单 (Tech Stack) <a class="header-anchor" href="#_02-技术栈清单-tech-stack" aria-label="Permalink to &quot;02. 技术栈清单 (Tech Stack)&quot;">​</a></h1><p>KeyApp 选用了最现代化的前端技术栈，以支撑 WebOS 的复杂性。</p><h2 id="core-framework" tabindex="-1">Core Framework <a class="header-anchor" href="#core-framework" aria-label="Permalink to &quot;Core Framework&quot;">​</a></h2><ul><li><strong>React 19</strong>: 利用最新的 Hooks 和并发特性。</li><li><strong>Vite 6</strong>: 极速构建工具，支持 ESM。</li><li><strong>TypeScript 5</strong>: 严格类型安全。</li></ul><h2 id="state-management" tabindex="-1">State Management <a class="header-anchor" href="#state-management" aria-label="Permalink to &quot;State Management&quot;">​</a></h2><ul><li><strong>TanStack Store</strong>: 轻量级、原子化的全局状态管理 (替代 Redux/Zustand)。</li><li><strong>TanStack Query (v5)</strong>: 异步数据管理，用于链上数据缓存和同步。</li></ul><h2 id="navigation-shell" tabindex="-1">Navigation &amp; Shell <a class="header-anchor" href="#navigation-shell" aria-label="Permalink to &quot;Navigation &amp; Shell&quot;">​</a></h2><ul><li><strong>Stackflow</strong>: 仿原生 iOS/Android 的路由和页面栈管理。 <ul><li><em>Why?</em> React Router 无法提供原生 App 的页面堆叠和手势体验。</li></ul></li></ul><h2 id="ui-system" tabindex="-1">UI System <a class="header-anchor" href="#ui-system" aria-label="Permalink to &quot;UI System&quot;">​</a></h2><ul><li><strong>Tailwind CSS 4</strong>: 原子化 CSS 引擎。</li><li><strong>shadcn/ui</strong>: 基于 Radix UI 的可定制组件库。</li><li><strong>Framer Motion / Motion</strong>: 负责复杂的 FLIP 动画和转场。</li></ul><h2 id="testing" tabindex="-1">Testing <a class="header-anchor" href="#testing" aria-label="Permalink to &quot;Testing&quot;">​</a></h2><ul><li><strong>Vitest</strong>: 单元测试 (兼容 Jest API)。</li><li><strong>Playwright</strong>: 端到端 (E2E) 测试。</li></ul><h2 id="architecture-patterns" tabindex="-1">Architecture Patterns <a class="header-anchor" href="#architecture-patterns" aria-label="Permalink to &quot;Architecture Patterns&quot;">​</a></h2><ul><li><strong>Dependency Injection (DI)</strong>: 通过 <code>context.tsx</code> 实现服务解耦。</li><li><strong>Adapter Pattern</strong>: 用于多链适配 (<code>chain-adapter</code>)。</li><li><strong>Actor Model</strong>: 用于 <code>miniapp-runtime</code> 的进程通信。</li></ul>',14)])])}const m=e(i,[["render",n]]);export{g as __pageData,m as default};
