import{_ as s,c as t,o as n,ag as l}from"./chunks/framework.B0we9iV-.js";const u=JSON.parse('{"title":"用户故事","description":"","frontmatter":{},"headers":[],"relativePath":"white-book/00-Manifesto/08-User-Stories.md","filePath":"white-book/00-Manifesto/08-User-Stories.md"}'),p={name:"white-book/00-Manifesto/08-User-Stories.md"};function i(e,a,o,r,d,c){return n(),t("div",null,[...a[0]||(a[0]=[l(`<h1 id="用户故事" tabindex="-1">用户故事 <a class="header-anchor" href="#用户故事" aria-label="Permalink to &quot;用户故事&quot;">​</a></h1><blockquote><p>定义完整的功能需求和验收标准</p></blockquote><hr><h2 id="epic-概览" tabindex="-1">Epic 概览 <a class="header-anchor" href="#epic-概览" aria-label="Permalink to &quot;Epic 概览&quot;">​</a></h2><p>BFM Pay 的功能需求组织为 10 个 Epic：</p><table tabindex="0"><thead><tr><th>Epic</th><th>名称</th><th>核心用户故事</th></tr></thead><tbody><tr><td>1</td><td><a href="#epic-1-我想拥有一个自己的钱包">拥有钱包</a></td><td>创建、导入钱包</td></tr><tr><td>2</td><td><a href="#epic-2-我想查看我的资产">查看资产</a></td><td>余额查看、链切换</td></tr><tr><td>3</td><td><a href="#epic-3-我想给别人转账">转账</a></td><td>发起转账、扫码</td></tr><tr><td>4</td><td><a href="#epic-4-我想收款">收款</a></td><td>展示收款码</td></tr><tr><td>5</td><td><a href="#epic-5-我想进行跨链质押">质押</a></td><td>Mint/Burn</td></tr><tr><td>6</td><td><a href="#epic-6-我想保护我的钱包安全">安全设置</a></td><td>应用锁、备份</td></tr><tr><td>7</td><td><a href="#epic-7-我想管理常用地址">地址管理</a></td><td>地址簿</td></tr><tr><td>8</td><td><a href="#epic-8-我想与-dapp-交互">DWEB 交互</a></td><td>授权、签名</td></tr><tr><td>9</td><td><a href="#epic-9-我想个性化我的钱包">个性化</a></td><td>语言、货币</td></tr><tr><td>10</td><td><a href="#epic-10-我的页面">我的</a></td><td>设置入口</td></tr></tbody></table><hr><h2 id="epic-1-我想拥有一个自己的钱包" tabindex="-1">Epic 1: 我想拥有一个自己的钱包 <a class="header-anchor" href="#epic-1-我想拥有一个自己的钱包" aria-label="Permalink to &quot;Epic 1: 我想拥有一个自己的钱包&quot;">​</a></h2><blockquote><p>作为一个新用户，我想创建自己的数字钱包，以便开始管理我的数字资产。</p></blockquote><h3 id="story-1-1-首次启动引导" tabindex="-1">Story 1.1: 首次启动引导 <a class="header-anchor" href="#story-1-1-首次启动引导" aria-label="Permalink to &quot;Story 1.1: 首次启动引导&quot;">​</a></h3><p><strong>场景</strong>：用户首次打开 App</p><p><strong>主线流程</strong>：</p><ol><li>展示产品价值介绍（3页轮播） <ul><li>&quot;便捷转账 - 随时随地管理资产&quot;</li><li>&quot;多链支持 - 无限可能&quot;</li><li>&quot;全面安全 - 资产更安全&quot;</li></ul></li><li>点击&quot;开始使用&quot;或&quot;我有钱包&quot;</li><li>进入创建/导入选择</li></ol><p><strong>验收标准</strong>：</p><ul><li>[ ] 轮播页最多 3 页，可跳过</li><li>[ ] 跳过后不再显示（记录到 localStorage）</li><li>[ ] 支持左右滑动切换</li></ul><hr><h3 id="story-1-2-创建新钱包" tabindex="-1">Story 1.2: 创建新钱包 <a class="header-anchor" href="#story-1-2-创建新钱包" aria-label="Permalink to &quot;Story 1.2: 创建新钱包&quot;">​</a></h3><p><strong>场景</strong>：用户选择创建新钱包</p><p><strong>主线流程</strong>：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>设置钱包锁 → 生成助记词 → 备份提示 → 验证助记词 → 创建成功</span></span></code></pre></div><p><strong>步骤详情</strong>：</p><table tabindex="0"><thead><tr><th>步骤</th><th>用户操作</th><th>系统响应</th></tr></thead><tbody><tr><td>1</td><td>绘制图案（至少4点）</td><td>实时显示连接点数</td></tr><tr><td>2</td><td>确认图案</td><td>检查一致性</td></tr><tr><td>3</td><td>点击下一步</td><td>生成 12 词助记词</td></tr><tr><td>4</td><td>点击&quot;显示助记词&quot;</td><td>展示助记词（默认模糊）</td></tr><tr><td>5</td><td>点击&quot;我已备份&quot;</td><td>进入验证步骤</td></tr><tr><td>6</td><td>填写 3 个随机位置的词</td><td>验证正确性</td></tr><tr><td>7</td><td>点击&quot;完成创建&quot;</td><td>派生多链地址，跳转首页</td></tr></tbody></table><p><strong>支线流程</strong>：</p><ul><li>用户点击&quot;稍后备份&quot; → 首页显示&quot;未备份&quot;警告</li></ul><p><strong>异常处理</strong>：</p><table tabindex="0"><thead><tr><th>异常</th><th>提示</th><th>处理</th></tr></thead><tbody><tr><td>图案点数不足</td><td>&quot;请至少连接4个点&quot;</td><td>禁用下一步</td></tr><tr><td>两次图案不一致</td><td>&quot;两次图案不一致&quot;</td><td>禁用下一步</td></tr><tr><td>助记词验证错误</td><td>&quot;第X个单词不正确&quot;</td><td>允许重试</td></tr></tbody></table><hr><h3 id="story-1-3-导入已有钱包" tabindex="-1">Story 1.3: 导入已有钱包 <a class="header-anchor" href="#story-1-3-导入已有钱包" aria-label="Permalink to &quot;Story 1.3: 导入已有钱包&quot;">​</a></h3><p><strong>场景</strong>：用户已有助记词或私钥</p><p><strong>助记词导入流程</strong>：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>选择词数 → 输入助记词 → 设置钱包锁 → 导入成功</span></span></code></pre></div><p><strong>私钥导入流程</strong>：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>选择链 → 输入私钥 → 设置钱包锁 → 导入成功</span></span></code></pre></div><p><strong>验收标准</strong>：</p><ul><li>[ ] 支持 12/24 词助记词</li><li>[ ] 支持助记词粘贴自动分词</li><li>[ ] 私钥导入仅导入单链地址</li><li>[ ] 导入后派生所有支持的链地址</li></ul><hr><h2 id="epic-2-我想查看我的资产" tabindex="-1">Epic 2: 我想查看我的资产 <a class="header-anchor" href="#epic-2-我想查看我的资产" aria-label="Permalink to &quot;Epic 2: 我想查看我的资产&quot;">​</a></h2><blockquote><p>作为钱包用户，我想随时查看我的资产情况，了解我有多少钱。</p></blockquote><h3 id="story-2-1-查看资产总览" tabindex="-1">Story 2.1: 查看资产总览 <a class="header-anchor" href="#story-2-1-查看资产总览" aria-label="Permalink to &quot;Story 2.1: 查看资产总览&quot;">​</a></h3><p><strong>场景</strong>：用户登录后进入首页</p><p><strong>页面结构</strong>：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>┌─────────────────────────────────┐</span></span>
<span class="line"><span>│  [钱包名称 ▼]     [链名称 ▼]    │  ← 钱包/链切换</span></span>
<span class="line"><span>├─────────────────────────────────┤</span></span>
<span class="line"><span>│                                 │</span></span>
<span class="line"><span>│     ┌───────────────────┐      │</span></span>
<span class="line"><span>│     │   钱包卡片         │      │</span></span>
<span class="line"><span>│     │   地址: 0x71C...  │      │</span></span>
<span class="line"><span>│     │   [复制] [二维码]  │      │</span></span>
<span class="line"><span>│     └───────────────────┘      │</span></span>
<span class="line"><span>│                                 │</span></span>
<span class="line"><span>│  [转账]   [铸造]   [收款]       │  ← 快捷操作</span></span>
<span class="line"><span>│                                 │</span></span>
<span class="line"><span>├─────────────────────────────────┤</span></span>
<span class="line"><span>│  [资产]  [DP]                   │  ← Tab 切换</span></span>
<span class="line"><span>├─────────────────────────────────┤</span></span>
<span class="line"><span>│  ┌─────────────────────────┐   │</span></span>
<span class="line"><span>│  │ ETH        10.5 ETH     │   │</span></span>
<span class="line"><span>│  │ USDT       1,000 USDT   │   │</span></span>
<span class="line"><span>│  │ ...                     │   │</span></span>
<span class="line"><span>│  └─────────────────────────┘   │</span></span>
<span class="line"><span>└─────────────────────────────────┘</span></span></code></pre></div><p><strong>验收标准</strong>：</p><ul><li>[ ] 下拉刷新更新余额</li><li>[ ] 点击代币进入详情页</li><li>[ ] 支持切换资产/DP 视图</li><li>[ ] 离线时显示缓存数据</li></ul><hr><h3 id="story-2-2-切换钱包-链" tabindex="-1">Story 2.2: 切换钱包/链 <a class="header-anchor" href="#story-2-2-切换钱包-链" aria-label="Permalink to &quot;Story 2.2: 切换钱包/链&quot;">​</a></h3><p><strong>钱包切换</strong>：</p><ol><li>点击钱包名称</li><li>底部弹出钱包列表</li><li>选择目标钱包</li><li>刷新首页数据</li></ol><p><strong>链切换</strong>：</p><ol><li>点击链名称</li><li>底部弹出链列表</li><li>选择目标链</li><li>刷新首页数据</li></ol><p><strong>验收标准</strong>：</p><ul><li>[ ] 保持最近选择的钱包/链</li><li>[ ] 切换时显示加载状态</li><li>[ ] 切换后余额自动更新</li></ul><hr><h2 id="epic-3-我想给别人转账" tabindex="-1">Epic 3: 我想给别人转账 <a class="header-anchor" href="#epic-3-我想给别人转账" aria-label="Permalink to &quot;Epic 3: 我想给别人转账&quot;">​</a></h2><blockquote><p>作为钱包用户，我想把代币转给别人，完成支付或转移。</p></blockquote><h3 id="story-3-1-发起转账" tabindex="-1">Story 3.1: 发起转账 <a class="header-anchor" href="#story-3-1-发起转账" aria-label="Permalink to &quot;Story 3.1: 发起转账&quot;">​</a></h3><p><strong>主线流程</strong>：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>输入地址 → 输入金额 → 确认详情 → 验证钱包锁 → 发送成功</span></span></code></pre></div><p><strong>页面结构</strong>：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>┌─────────────────────────────────┐</span></span>
<span class="line"><span>│  ←  发送                        │</span></span>
<span class="line"><span>├─────────────────────────────────┤</span></span>
<span class="line"><span>│                                 │</span></span>
<span class="line"><span>│  当前链: Ethereum               │</span></span>
<span class="line"><span>│                                 │</span></span>
<span class="line"><span>│  收款地址                        │</span></span>
<span class="line"><span>│  ┌─────────────────────────┐   │</span></span>
<span class="line"><span>│  │ 输入或粘贴地址      [扫] │   │</span></span>
<span class="line"><span>│  └─────────────────────────┘   │</span></span>
<span class="line"><span>│                                 │</span></span>
<span class="line"><span>│  金额                           │</span></span>
<span class="line"><span>│  ┌─────────────────────────┐   │</span></span>
<span class="line"><span>│  │ 0.00              [全部]│   │</span></span>
<span class="line"><span>│  └─────────────────────────┘   │</span></span>
<span class="line"><span>│  可用: 10.5 ETH                 │</span></span>
<span class="line"><span>│                                 │</span></span>
<span class="line"><span>│  网络费用: ~0.002 ETH           │</span></span>
<span class="line"><span>│                                 │</span></span>
<span class="line"><span>│  ┌─────────────────────────┐   │</span></span>
<span class="line"><span>│  │       确认发送           │   │</span></span>
<span class="line"><span>│  └─────────────────────────┘   │</span></span>
<span class="line"><span>└─────────────────────────────────┘</span></span></code></pre></div><p><strong>异常处理</strong>：</p><table tabindex="0"><thead><tr><th>异常</th><th>提示</th><th>处理</th></tr></thead><tbody><tr><td>地址格式错误</td><td>&quot;地址格式不正确&quot;</td><td>禁用发送</td></tr><tr><td>余额不足</td><td>&quot;余额不足&quot;</td><td>禁用发送</td></tr><tr><td>手续费不足</td><td>&quot;ETH 余额不足以支付手续费&quot;</td><td>提示充值</td></tr><tr><td>收款地址是自己</td><td>&quot;收款地址不能与发送地址相同&quot;</td><td>禁用发送</td></tr></tbody></table><hr><h3 id="story-3-2-扫码转账" tabindex="-1">Story 3.2: 扫码转账 <a class="header-anchor" href="#story-3-2-扫码转账" aria-label="Permalink to &quot;Story 3.2: 扫码转账&quot;">​</a></h3><p><strong>主线流程</strong>：</p><ol><li>点击扫码按钮</li><li>相机扫描二维码</li><li>解析地址（可能包含金额）</li><li>自动填入表单</li><li>继续转账流程</li></ol><p><strong>验收标准</strong>：</p><ul><li>[ ] 支持从相册选择</li><li>[ ] 支持解析带金额的二维码</li><li>[ ] 扫描超时 60 秒提示</li></ul><hr><h2 id="epic-4-我想收款" tabindex="-1">Epic 4: 我想收款 <a class="header-anchor" href="#epic-4-我想收款" aria-label="Permalink to &quot;Epic 4: 我想收款&quot;">​</a></h2><blockquote><p>作为钱包用户，我想让别人给我转账。</p></blockquote><h3 id="story-4-1-展示收款码" tabindex="-1">Story 4.1: 展示收款码 <a class="header-anchor" href="#story-4-1-展示收款码" aria-label="Permalink to &quot;Story 4.1: 展示收款码&quot;">​</a></h3><p><strong>页面结构</strong>：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>┌─────────────────────────────────┐</span></span>
<span class="line"><span>│  ←  收款                        │</span></span>
<span class="line"><span>├─────────────────────────────────┤</span></span>
<span class="line"><span>│                                 │</span></span>
<span class="line"><span>│     ┌───────────────────┐      │</span></span>
<span class="line"><span>│     │                   │      │</span></span>
<span class="line"><span>│     │   [二维码图片]     │      │</span></span>
<span class="line"><span>│     │                   │      │</span></span>
<span class="line"><span>│     └───────────────────┘      │</span></span>
<span class="line"><span>│                                 │</span></span>
<span class="line"><span>│  0x71C7656EC7ab88b098defB...   │</span></span>
<span class="line"><span>│                                 │</span></span>
<span class="line"><span>│        [复制地址]               │</span></span>
<span class="line"><span>│                                 │</span></span>
<span class="line"><span>│  当前链: Ethereum               │</span></span>
<span class="line"><span>│                                 │</span></span>
<span class="line"><span>└─────────────────────────────────┘</span></span></code></pre></div><p><strong>验收标准</strong>：</p><ul><li>[ ] 二维码包含完整地址</li><li>[ ] 支持复制地址</li><li>[ ] 支持保存二维码到相册</li><li>[ ] 可选设置收款金额</li></ul><hr><h2 id="epic-5-我想进行跨链质押" tabindex="-1">Epic 5: 我想进行跨链质押 <a class="header-anchor" href="#epic-5-我想进行跨链质押" aria-label="Permalink to &quot;Epic 5: 我想进行跨链质押&quot;">​</a></h2><blockquote><p>作为进阶用户，我想通过质押外链代币获取内链代币。</p></blockquote><h3 id="story-5-1-mint-代币" tabindex="-1">Story 5.1: Mint 代币 <a class="header-anchor" href="#story-5-1-mint-代币" aria-label="Permalink to &quot;Story 5.1: Mint 代币&quot;">​</a></h3><p><strong>场景</strong>：用户想用 USDT 铸造 USDM</p><p><strong>主线流程</strong>：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>选择质押代币 → 选择获取代币 → 输入数量 → 确认 → 等待完成</span></span></code></pre></div><p><strong>前置条件</strong>：</p><ul><li>钱包必须同时支持质押链和目标链</li></ul><p><strong>验收标准</strong>：</p><ul><li>[ ] 显示 1:1 兑换关系</li><li>[ ] 显示预估到账时间</li><li>[ ] 支持查看质押历史</li></ul><hr><h2 id="epic-6-我想保护我的钱包安全" tabindex="-1">Epic 6: 我想保护我的钱包安全 <a class="header-anchor" href="#epic-6-我想保护我的钱包安全" aria-label="Permalink to &quot;Epic 6: 我想保护我的钱包安全&quot;">​</a></h2><blockquote><p>作为用户，我希望我的钱包是安全的，不会被他人使用。</p></blockquote><h3 id="story-6-1-设置应用锁" tabindex="-1">Story 6.1: 设置应用锁 <a class="header-anchor" href="#story-6-1-设置应用锁" aria-label="Permalink to &quot;Story 6.1: 设置应用锁&quot;">​</a></h3><p><strong>主线流程</strong>：</p><ol><li>进入设置 → 应用锁</li><li>开启钱包锁</li><li>绘制钱包锁图案验证</li><li>设置成功</li></ol><p><strong>验收标准</strong>：</p><ul><li>[ ] 支持钱包锁（九宫格图案锁）</li><li>[ ] 支持指纹锁（需先开启钱包锁）</li><li>[ ] 支持指纹支付</li></ul><hr><h3 id="story-6-2-备份助记词" tabindex="-1">Story 6.2: 备份助记词 <a class="header-anchor" href="#story-6-2-备份助记词" aria-label="Permalink to &quot;Story 6.2: 备份助记词&quot;">​</a></h3><p><strong>主线流程</strong>：</p><ol><li>进入钱包管理 → 备份</li><li>绘制钱包锁图案验证</li><li>显示安全提示</li><li>展示助记词</li><li>完成确认验证</li></ol><p><strong>验收标准</strong>：</p><ul><li>[ ] 验证图案后才能查看</li><li>[ ] 提醒用户确保周围无人</li><li>[ ] 完成后消除&quot;未备份&quot;警告</li></ul><hr><h2 id="epic-7-我想管理常用地址" tabindex="-1">Epic 7: 我想管理常用地址 <a class="header-anchor" href="#epic-7-我想管理常用地址" aria-label="Permalink to &quot;Epic 7: 我想管理常用地址&quot;">​</a></h2><blockquote><p>作为频繁转账的用户，我不想每次都手动输入地址。</p></blockquote><h3 id="story-7-1-地址簿管理" tabindex="-1">Story 7.1: 地址簿管理 <a class="header-anchor" href="#story-7-1-地址簿管理" aria-label="Permalink to &quot;Story 7.1: 地址簿管理&quot;">​</a></h3><p><strong>功能</strong>：</p><ul><li>添加地址（名称 + 链 + 地址）</li><li>编辑地址</li><li>删除地址</li><li>转账时选择地址</li></ul><p><strong>验收标准</strong>：</p><ul><li>[ ] 按链分类显示</li><li>[ ] 支持搜索</li><li>[ ] 转账成功后提示保存</li></ul><hr><h2 id="epic-8-我想与-dapp-交互" tabindex="-1">Epic 8: 我想与 DApp 交互 <a class="header-anchor" href="#epic-8-我想与-dapp-交互" aria-label="Permalink to &quot;Epic 8: 我想与 DApp 交互&quot;">​</a></h2><blockquote><p>作为 Web3 用户，我想用钱包连接和使用 DApp。</p></blockquote><h3 id="story-8-1-地址授权" tabindex="-1">Story 8.1: 地址授权 <a class="header-anchor" href="#story-8-1-地址授权" aria-label="Permalink to &quot;Story 8.1: 地址授权&quot;">​</a></h3><p><strong>场景</strong>：DApp 请求获取钱包地址</p><p><strong>流程</strong>：</p><ol><li>DApp 发起 <code>getAddress</code> 请求</li><li>钱包弹出授权页</li><li>显示 DApp 信息和请求内容</li><li>用户选择授权范围</li><li>验证钱包锁确认</li><li>返回地址信息</li></ol><p><strong>授权类型</strong>：</p><ul><li>单钱包授权：当前钱包的所有链地址</li><li>单链授权：指定链的所有钱包地址</li><li>全部授权：所有钱包的所有地址</li></ul><hr><h3 id="story-8-2-签名授权" tabindex="-1">Story 8.2: 签名授权 <a class="header-anchor" href="#story-8-2-签名授权" aria-label="Permalink to &quot;Story 8.2: 签名授权&quot;">​</a></h3><p><strong>签名类型</strong>：</p><table tabindex="0"><thead><tr><th>类型</th><th>场景</th><th>风险等级</th></tr></thead><tbody><tr><td>消息签名</td><td>登录验证</td><td>低</td></tr><tr><td>转账签名</td><td>发起转账</td><td>高</td></tr><tr><td>合约签名</td><td>智能合约交互</td><td>高</td></tr></tbody></table><p><strong>验收标准</strong>：</p><ul><li>[ ] 显示 DApp 来源信息</li><li>[ ] 转账签名显示完整交易详情</li><li>[ ] 高风险操作需验证钱包锁</li></ul><hr><h2 id="epic-9-我想个性化我的钱包" tabindex="-1">Epic 9: 我想个性化我的钱包 <a class="header-anchor" href="#epic-9-我想个性化我的钱包" aria-label="Permalink to &quot;Epic 9: 我想个性化我的钱包&quot;">​</a></h2><blockquote><p>作为用户，我想按自己的偏好使用钱包。</p></blockquote><h3 id="story-9-1-切换语言" tabindex="-1">Story 9.1: 切换语言 <a class="header-anchor" href="#story-9-1-切换语言" aria-label="Permalink to &quot;Story 9.1: 切换语言&quot;">​</a></h3><p><strong>支持语言</strong>：</p><ul><li>简体中文 (zh-CN)</li><li>繁体中文 (zh-TW)</li><li>English (en)</li><li>日本語 (ja)</li><li>한국어 (ko)</li><li>العربية (ar) - RTL</li></ul><p><strong>验收标准</strong>：</p><ul><li>[ ] 切换后立即生效</li><li>[ ] 记住用户选择</li><li>[ ] 首次自动检测系统语言</li></ul><hr><h3 id="story-9-2-设置货币单位" tabindex="-1">Story 9.2: 设置货币单位 <a class="header-anchor" href="#story-9-2-设置货币单位" aria-label="Permalink to &quot;Story 9.2: 设置货币单位&quot;">​</a></h3><p><strong>支持货币</strong>： USD, CNY, EUR, JPY, KRW 等</p><p><strong>验收标准</strong>：</p><ul><li>[ ] 首页显示换算后的法币价值</li><li>[ ] 汇率定期更新</li><li>[ ] 默认使用 USD</li></ul><hr><h2 id="epic-10-我的-页面" tabindex="-1">Epic 10: &quot;我的&quot;页面 <a class="header-anchor" href="#epic-10-我的-页面" aria-label="Permalink to &quot;Epic 10: &quot;我的&quot;页面&quot;">​</a></h2><blockquote><p>作为用户，我需要一个统一的入口管理所有设置。</p></blockquote><h3 id="页面结构" tabindex="-1">页面结构 <a class="header-anchor" href="#页面结构" aria-label="Permalink to &quot;页面结构&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>┌─────────────────────────────────┐</span></span>
<span class="line"><span>│  👤 钱包名称                     │</span></span>
<span class="line"><span>│     0x71C7...976F               │</span></span>
<span class="line"><span>├─────────────────────────────────┤</span></span>
<span class="line"><span>│  📁 钱包管理                  &gt;  │</span></span>
<span class="line"><span>│  📒 地址簿                    &gt;  │</span></span>
<span class="line"><span>├─────────────────────────────────┤</span></span>
<span class="line"><span>│  🔒 应用锁                    &gt;  │</span></span>
<span class="line"><span>│  🔑 修改密码                  &gt;  │</span></span>
<span class="line"><span>│  📝 备份助记词              🔴 &gt; │ ← 未备份红点</span></span>
<span class="line"><span>├─────────────────────────────────┤</span></span>
<span class="line"><span>│  🌐 语言              简体中文 &gt; │</span></span>
<span class="line"><span>│  💰 货币单位              CNY &gt; │</span></span>
<span class="line"><span>│  ⛓️ 链配置                    &gt;  │</span></span>
<span class="line"><span>├─────────────────────────────────┤</span></span>
<span class="line"><span>│  ❓ 帮助与反馈                &gt;  │</span></span>
<span class="line"><span>│  ℹ️ 关于                      &gt;  │</span></span>
<span class="line"><span>└─────────────────────────────────┘</span></span></code></pre></div><hr><h2 id="本章小结" tabindex="-1">本章小结 <a class="header-anchor" href="#本章小结" aria-label="Permalink to &quot;本章小结&quot;">​</a></h2><ul><li>10 个 Epic 覆盖完整用户旅程</li><li>每个 Story 包含主线流程、支线流程、异常处理</li><li>用户心智考量贯穿每个设计决策</li><li>验收标准明确，便于测试验证</li></ul><hr><h2 id="相关文档" tabindex="-1">相关文档 <a class="header-anchor" href="#相关文档" aria-label="Permalink to &quot;相关文档&quot;">​</a></h2><ul><li><a href="./05-Product-Vision.html">产品愿景</a></li><li><a href="./../12-Shell-Guide/00-Navigation-Map.html">导航地图</a></li><li><a href="./../03-UI-Ref/05-Components/08-Onboarding.html">Onboarding 组件</a></li></ul>`,148)])])}const b=s(p,[["render",i]]);export{u as __pageData,b as default};
