import{_ as t,c as l,o as s,ag as n}from"./chunks/framework.B0we9iV-.js";const c=JSON.parse('{"title":"第二章：用户画像","description":"","frontmatter":{},"headers":[],"relativePath":"white-book/01-产品篇/02-用户画像/index.md","filePath":"white-book/01-产品篇/02-用户画像/index.md"}'),i={name:"white-book/01-产品篇/02-用户画像/index.md"};function e(o,a,p,r,d,h){return s(),l("div",null,[...a[0]||(a[0]=[n(`<h1 id="第二章-用户画像" tabindex="-1">第二章：用户画像 <a class="header-anchor" href="#第二章-用户画像" aria-label="Permalink to &quot;第二章：用户画像&quot;">​</a></h1><blockquote><p>深入理解我们的用户是谁，他们需要什么</p></blockquote><hr><h2 id="_2-1-主要用户群体" tabindex="-1">2.1 主要用户群体 <a class="header-anchor" href="#_2-1-主要用户群体" aria-label="Permalink to &quot;2.1 主要用户群体&quot;">​</a></h2><h3 id="👤-新手小白-alice" tabindex="-1">👤 新手小白 (Alice) <a class="header-anchor" href="#👤-新手小白-alice" aria-label="Permalink to &quot;👤 新手小白 (Alice)&quot;">​</a></h3><p><strong>基本信息</strong></p><ul><li>年龄：28岁</li><li>职业：设计师</li><li>技术水平：普通用户，熟悉手机 App</li></ul><p><strong>背景故事</strong></p><blockquote><p>Alice 听朋友说起加密货币，想尝试但觉得技术门槛高。她用过支付宝和微信支付，但从未接触过区块链。</p></blockquote><p><strong>痛点</strong></p><ul><li>😰 害怕操作失误丢失资产</li><li>😕 不理解&quot;助记词&quot;&quot;Gas 费&quot;等概念</li><li>🤔 担心被骗或黑客攻击</li></ul><p><strong>期望</strong></p><ul><li>✅ 像支付宝一样简单</li><li>✅ 有清晰的操作引导</li><li>✅ 出问题能找到帮助</li></ul><p><strong>心智模型</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>钱包 = 银行账户</span></span>
<span class="line"><span>助记词 = 银行卡密码</span></span>
<span class="line"><span>转账 = 微信转账</span></span></code></pre></div><hr><h3 id="👤-进阶用户-bob" tabindex="-1">👤 进阶用户 (Bob) <a class="header-anchor" href="#👤-进阶用户-bob" aria-label="Permalink to &quot;👤 进阶用户 (Bob)&quot;">​</a></h3><p><strong>基本信息</strong></p><ul><li>年龄：35岁</li><li>职业：程序员</li><li>技术水平：熟悉区块链，使用过多款钱包</li></ul><p><strong>背景故事</strong></p><blockquote><p>Bob 已有多个链的资产，使用过 MetaMask、Trust Wallet、imToken 等钱包。他对技术有一定了解，但讨厌在多个钱包间切换。</p></blockquote><p><strong>痛点</strong></p><ul><li>😤 需要在多个钱包间切换</li><li>😫 跨链操作繁琐</li><li>🙄 重复的设置和备份流程</li></ul><p><strong>期望</strong></p><ul><li>✅ 一个钱包管理所有链</li><li>✅ 快速完成日常操作</li><li>✅ 高级功能不被隐藏</li></ul><p><strong>心智模型</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>钱包 = 资产管理工具</span></span>
<span class="line"><span>效率 = 最重要的事</span></span>
<span class="line"><span>安全 = 基本要求</span></span></code></pre></div><hr><h3 id="👤-活跃交易者-carol" tabindex="-1">👤 活跃交易者 (Carol) <a class="header-anchor" href="#👤-活跃交易者-carol" aria-label="Permalink to &quot;👤 活跃交易者 (Carol)&quot;">​</a></h3><p><strong>基本信息</strong></p><ul><li>年龄：30岁</li><li>职业：交易员</li><li>技术水平：精通区块链，高频交易</li></ul><p><strong>背景故事</strong></p><blockquote><p>Carol 每天进行多笔转账和交易，需要快速确认交易状态。她最担心的是转错地址或被钓鱼攻击。</p></blockquote><p><strong>痛点</strong></p><ul><li>😱 担心转错地址</li><li>⏱️ 需要快速确认交易状态</li><li>🎣 警惕钓鱼和欺诈</li></ul><p><strong>期望</strong></p><ul><li>✅ 地址簿管理常用地址</li><li>✅ 实时交易状态更新</li><li>✅ 完整的历史记录</li></ul><p><strong>心智模型</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>钱包 = 交易终端</span></span>
<span class="line"><span>安全 = 生命线</span></span>
<span class="line"><span>速度 = 金钱</span></span></code></pre></div><hr><h2 id="_2-2-用户心智模型" tabindex="-1">2.2 用户心智模型 <a class="header-anchor" href="#_2-2-用户心智模型" aria-label="Permalink to &quot;2.2 用户心智模型&quot;">​</a></h2><h3 id="关键认知映射" tabindex="-1">关键认知映射 <a class="header-anchor" href="#关键认知映射" aria-label="Permalink to &quot;关键认知映射&quot;">​</a></h3><table tabindex="0"><thead><tr><th>用户认知</th><th>技术概念</th><th>产品设计对应</th></tr></thead><tbody><tr><td>&quot;钱包 = 存钱的地方&quot;</td><td>密钥管理</td><td>首页突出显示余额，像银行 App</td></tr><tr><td>&quot;地址 = 账号&quot;</td><td>公钥哈希</td><td>简化地址展示，提供复制和二维码</td></tr><tr><td>&quot;助记词 = 最高密码&quot;</td><td>BIP39 种子</td><td>强调备份重要性，通俗类比解释</td></tr><tr><td>&quot;转账要确认&quot;</td><td>签名广播</td><td>双重确认机制，显示手续费</td></tr><tr><td>&quot;不同链 = 不同银行&quot;</td><td>多链架构</td><td>清晰的链切换，统一的操作体验</td></tr></tbody></table><h3 id="心智模型图" tabindex="-1">心智模型图 <a class="header-anchor" href="#心智模型图" aria-label="Permalink to &quot;心智模型图&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>用户视角                          技术视角</span></span>
<span class="line"><span>─────────────                    ─────────────</span></span>
<span class="line"><span>   钱包          ←────────→       密钥对</span></span>
<span class="line"><span>    │                              │</span></span>
<span class="line"><span>    ▼                              ▼</span></span>
<span class="line"><span>  账户/地址      ←────────→       公钥地址</span></span>
<span class="line"><span>    │                              │</span></span>
<span class="line"><span>    ▼                              ▼</span></span>
<span class="line"><span>   余额          ←────────→      链上状态</span></span>
<span class="line"><span>    │                              │</span></span>
<span class="line"><span>    ▼                              ▼</span></span>
<span class="line"><span>   转账          ←────────→      签名交易</span></span></code></pre></div><hr><h2 id="_2-3-用户旅程阶段" tabindex="-1">2.3 用户旅程阶段 <a class="header-anchor" href="#_2-3-用户旅程阶段" aria-label="Permalink to &quot;2.3 用户旅程阶段&quot;">​</a></h2><h3 id="阶段一-认知" tabindex="-1">阶段一：认知 <a class="header-anchor" href="#阶段一-认知" aria-label="Permalink to &quot;阶段一：认知&quot;">​</a></h3><blockquote><p>&quot;这是什么？对我有什么用？&quot;</p></blockquote><p><strong>用户状态</strong></p><ul><li>第一次听说或看到 BFM Pay</li><li>可能来自朋友推荐或应用商店</li></ul><p><strong>设计重点</strong></p><ul><li>清晰的价值主张</li><li>简洁的首屏信息</li><li>降低认知负担</li></ul><hr><h3 id="阶段二-入门" tabindex="-1">阶段二：入门 <a class="header-anchor" href="#阶段二-入门" aria-label="Permalink to &quot;阶段二：入门&quot;">​</a></h3><blockquote><p>&quot;我要怎么开始？&quot;</p></blockquote><p><strong>用户状态</strong></p><ul><li>决定尝试，开始创建或导入钱包</li><li>最容易流失的阶段</li></ul><p><strong>设计重点</strong></p><ul><li>引导式流程</li><li>即时反馈</li><li>允许跳过非必要步骤</li></ul><hr><h3 id="阶段三-日常使用" tabindex="-1">阶段三：日常使用 <a class="header-anchor" href="#阶段三-日常使用" aria-label="Permalink to &quot;阶段三：日常使用&quot;">​</a></h3><blockquote><p>&quot;我需要完成一个任务&quot;</p></blockquote><p><strong>用户状态</strong></p><ul><li>已有钱包，进行日常操作</li><li>转账、收款、查看余额</li></ul><p><strong>设计重点</strong></p><ul><li>高效的操作路径</li><li>减少点击次数</li><li>记住用户偏好</li></ul><hr><h3 id="阶段四-进阶探索" tabindex="-1">阶段四：进阶探索 <a class="header-anchor" href="#阶段四-进阶探索" aria-label="Permalink to &quot;阶段四：进阶探索&quot;">​</a></h3><blockquote><p>&quot;还有什么高级功能？&quot;</p></blockquote><p><strong>用户状态</strong></p><ul><li>熟悉基础功能</li><li>探索质押、DApp 连接等</li></ul><p><strong>设计重点</strong></p><ul><li>功能可发现性</li><li>不干扰基础体验</li><li>渐进式披露</li></ul><hr><h3 id="阶段五-问题解决" tabindex="-1">阶段五：问题解决 <a class="header-anchor" href="#阶段五-问题解决" aria-label="Permalink to &quot;阶段五：问题解决&quot;">​</a></h3><blockquote><p>&quot;出问题了，怎么办？&quot;</p></blockquote><p><strong>用户状态</strong></p><ul><li>遇到错误或困惑</li><li>寻求帮助</li></ul><p><strong>设计重点</strong></p><ul><li>清晰的错误信息</li><li>可访问的帮助入口</li><li>自助解决方案</li></ul><hr><h2 id="_2-4-用户优先级" tabindex="-1">2.4 用户优先级 <a class="header-anchor" href="#_2-4-用户优先级" aria-label="Permalink to &quot;2.4 用户优先级&quot;">​</a></h2><h3 id="功能优先级矩阵" tabindex="-1">功能优先级矩阵 <a class="header-anchor" href="#功能优先级矩阵" aria-label="Permalink to &quot;功能优先级矩阵&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>                    高频</span></span>
<span class="line"><span>                     │</span></span>
<span class="line"><span>        ┌────────────┼────────────┐</span></span>
<span class="line"><span>        │   转账     │   查看余额  │</span></span>
<span class="line"><span>        │   收款     │   切换链    │</span></span>
<span class="line"><span>   高   │────────────┼────────────│   低</span></span>
<span class="line"><span>  价值  │   备份     │   设置     │  价值</span></span>
<span class="line"><span>        │   DApp授权 │   历史记录  │</span></span>
<span class="line"><span>        │────────────┼────────────│</span></span>
<span class="line"><span>        │   质押     │   地址簿    │</span></span>
<span class="line"><span>        │   导入钱包 │   网络设置  │</span></span>
<span class="line"><span>        └────────────┼────────────┘</span></span>
<span class="line"><span>                     │</span></span>
<span class="line"><span>                    低频</span></span></code></pre></div><h3 id="开发优先级" tabindex="-1">开发优先级 <a class="header-anchor" href="#开发优先级" aria-label="Permalink to &quot;开发优先级&quot;">​</a></h3><table tabindex="0"><thead><tr><th>优先级</th><th>功能</th><th>目标用户</th></tr></thead><tbody><tr><td>P0</td><td>创建/导入钱包、查看余额、转账、收款</td><td>所有用户</td></tr><tr><td>P1</td><td>多链切换、交易历史、备份助记词</td><td>所有用户</td></tr><tr><td>P2</td><td>地址簿、应用锁、指纹支付</td><td>进阶用户</td></tr><tr><td>P3</td><td>质押、DApp 授权、自定义网络</td><td>活跃用户</td></tr></tbody></table><hr><h2 id="_2-5-竞品对比中的用户需求" tabindex="-1">2.5 竞品对比中的用户需求 <a class="header-anchor" href="#_2-5-竞品对比中的用户需求" aria-label="Permalink to &quot;2.5 竞品对比中的用户需求&quot;">​</a></h2><h3 id="用户痛点对比" tabindex="-1">用户痛点对比 <a class="header-anchor" href="#用户痛点对比" aria-label="Permalink to &quot;用户痛点对比&quot;">​</a></h3><table tabindex="0"><thead><tr><th>痛点</th><th>MetaMask</th><th>Trust Wallet</th><th>BFM Pay 方案</th></tr></thead><tbody><tr><td>助记词理解困难</td><td>无解释</td><td>简单提示</td><td>通俗类比 + 引导</td></tr><tr><td>多链管理混乱</td><td>手动添加网络</td><td>预置网络</td><td>统一的链切换面板</td></tr><tr><td>转账地址输入</td><td>纯手动</td><td>地址簿</td><td>扫码 + 地址簿 + 历史</td></tr><tr><td>Gas 费理解</td><td>技术术语</td><td>简化显示</td><td>&quot;网络费用&quot; + 预估法币</td></tr></tbody></table><hr><h2 id="本章小结" tabindex="-1">本章小结 <a class="header-anchor" href="#本章小结" aria-label="Permalink to &quot;本章小结&quot;">​</a></h2><ul><li>三类核心用户：新手小白、进阶用户、活跃交易者</li><li>用户心智模型决定产品设计语言</li><li>用户旅程分五个阶段，每阶段有不同设计重点</li><li>功能开发按用户价值和使用频率确定优先级</li></ul><hr><h2 id="下一章" tabindex="-1">下一章 <a class="header-anchor" href="#下一章" aria-label="Permalink to &quot;下一章&quot;">​</a></h2><p>继续阅读 <a href="./../03-用户故事/">第三章：用户故事</a>，了解具体的功能需求和验收标准。</p>`,97)])])}const b=t(i,[["render",e]]);export{c as __pageData,b as default};
