import{_ as t,c as l,o as n,ag as e}from"./chunks/framework.B0we9iV-.js";const b=JSON.parse('{"title":"第六章：设计原则","description":"","frontmatter":{},"headers":[],"relativePath":"white-book/02-设计篇/03-设计原则/index.md","filePath":"white-book/02-设计篇/03-设计原则/index.md"}'),s={name:"white-book/02-设计篇/03-设计原则/index.md"};function i(d,a,r,o,h,p){return n(),l("div",null,[...a[0]||(a[0]=[e(`<h1 id="第六章-设计原则" tabindex="-1">第六章：设计原则 <a class="header-anchor" href="#第六章-设计原则" aria-label="Permalink to &quot;第六章：设计原则&quot;">​</a></h1><blockquote><p>定义设计决策的指导原则</p></blockquote><hr><h2 id="_6-1-信任优先" tabindex="-1">6.1 信任优先 <a class="header-anchor" href="#_6-1-信任优先" aria-label="Permalink to &quot;6.1 信任优先&quot;">​</a></h2><p>钱包是用户管理资产的工具，信任是最重要的基础。</p><h3 id="透明" tabindex="-1">透明 <a class="header-anchor" href="#透明" aria-label="Permalink to &quot;透明&quot;">​</a></h3><p><strong>原则</strong>：所有操作都清晰展示细节</p><table tabindex="0"><thead><tr><th>场景</th><th>透明设计</th></tr></thead><tbody><tr><td>转账</td><td>显示完整地址、金额、手续费</td></tr><tr><td>授权</td><td>显示 DApp 来源、请求权限</td></tr><tr><td>签名</td><td>显示签名内容（消息/交易）</td></tr></tbody></table><p><strong>示例</strong>：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>转账确认</span></span>
<span class="line"><span></span></span>
<span class="line"><span>发送      0x71C7656EC7ab88b098defB751B7401B5f6d8976F</span></span>
<span class="line"><span>接收      0xabcd...1234</span></span>
<span class="line"><span></span></span>
<span class="line"><span>金额      1.5 ETH</span></span>
<span class="line"><span>手续费    0.002 ETH (~$4.50)</span></span>
<span class="line"><span>─────────────────────────────</span></span>
<span class="line"><span>总计      1.502 ETH</span></span>
<span class="line"><span></span></span>
<span class="line"><span>[取消]                    [确认发送]</span></span></code></pre></div><h3 id="可控" tabindex="-1">可控 <a class="header-anchor" href="#可控" aria-label="Permalink to &quot;可控&quot;">​</a></h3><p><strong>原则</strong>：用户始终可以取消、返回、重试</p><table tabindex="0"><thead><tr><th>场景</th><th>可控设计</th></tr></thead><tbody><tr><td>多步骤流程</td><td>每步都可返回上一步</td></tr><tr><td>弹窗操作</td><td>可点击外部或关闭按钮关闭</td></tr><tr><td>长时操作</td><td>显示进度，可取消</td></tr></tbody></table><h3 id="安全提示" tabindex="-1">安全提示 <a class="header-anchor" href="#安全提示" aria-label="Permalink to &quot;安全提示&quot;">​</a></h3><p><strong>原则</strong>：在敏感操作前给予警告</p><p><strong>警告级别</strong>：</p><table tabindex="0"><thead><tr><th>级别</th><th>样式</th><th>场景</th></tr></thead><tbody><tr><td>提示</td><td>蓝色</td><td>一般信息</td></tr><tr><td>警告</td><td>黄色</td><td>需要注意</td></tr><tr><td>危险</td><td>红色</td><td>高风险操作</td></tr></tbody></table><p><strong>示例</strong>：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>⚠️ 警告</span></span>
<span class="line"><span></span></span>
<span class="line"><span>助记词是恢复钱包的唯一方式。</span></span>
<span class="line"><span>一旦丢失，资产将无法找回。</span></span>
<span class="line"><span></span></span>
<span class="line"><span>请确保：</span></span>
<span class="line"><span>• 周围没有其他人</span></span>
<span class="line"><span>• 不要截图保存</span></span>
<span class="line"><span>• 用纸笔抄写备份</span></span>
<span class="line"><span></span></span>
<span class="line"><span>[取消]            [我已了解，继续]</span></span></code></pre></div><hr><h2 id="_6-2-渐进式复杂度" tabindex="-1">6.2 渐进式复杂度 <a class="header-anchor" href="#_6-2-渐进式复杂度" aria-label="Permalink to &quot;6.2 渐进式复杂度&quot;">​</a></h2><h3 id="新手友好" tabindex="-1">新手友好 <a class="header-anchor" href="#新手友好" aria-label="Permalink to &quot;新手友好&quot;">​</a></h3><p><strong>原则</strong>：核心功能零学习成本</p><p><strong>实现方式</strong>：</p><ul><li>首页直接展示余额和快捷操作</li><li>转账流程最少只需 2 步</li><li>收款一键展示二维码</li></ul><h3 id="进阶可选" tabindex="-1">进阶可选 <a class="header-anchor" href="#进阶可选" aria-label="Permalink to &quot;进阶可选&quot;">​</a></h3><p><strong>原则</strong>：高级功能不干扰基础体验</p><p><strong>实现方式</strong>：</p><ul><li>质押、DApp 在首页不强调</li><li>高级设置放在二级菜单</li><li>功能按需加载</li></ul><h3 id="专家高效" tabindex="-1">专家高效 <a class="header-anchor" href="#专家高效" aria-label="Permalink to &quot;专家高效&quot;">​</a></h3><p><strong>原则</strong>：提供快捷操作给熟练用户</p><p><strong>实现方式</strong>：</p><ul><li>地址簿快速选择</li><li>最近转账记录</li><li>手势操作支持</li></ul><hr><h2 id="_6-3-错误预防" tabindex="-1">6.3 错误预防 <a class="header-anchor" href="#_6-3-错误预防" aria-label="Permalink to &quot;6.3 错误预防&quot;">​</a></h2><h3 id="格式验证" tabindex="-1">格式验证 <a class="header-anchor" href="#格式验证" aria-label="Permalink to &quot;格式验证&quot;">​</a></h3><p><strong>原则</strong>：实时校验输入格式</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>收款地址</span></span>
<span class="line"><span>┌───────────────────────────────┐</span></span>
<span class="line"><span>│ 0x71C7656EC7ab88b098defB751B  │  ✓ 格式正确</span></span>
<span class="line"><span>└───────────────────────────────┘</span></span>
<span class="line"><span></span></span>
<span class="line"><span>收款地址</span></span>
<span class="line"><span>┌───────────────────────────────┐</span></span>
<span class="line"><span>│ invalid_address               │  ✗</span></span>
<span class="line"><span>└───────────────────────────────┘</span></span>
<span class="line"><span>  ⚠️ 地址格式不正确</span></span></code></pre></div><h3 id="二次确认" tabindex="-1">二次确认 <a class="header-anchor" href="#二次确认" aria-label="Permalink to &quot;二次确认&quot;">​</a></h3><p><strong>原则</strong>：大额/异常操作需确认</p><p><strong>需要二次确认的场景</strong>：</p><ul><li>转账金额超过阈值</li><li>删除钱包</li><li>导出私钥</li><li>首次向新地址转账</li></ul><h3 id="可恢复" tabindex="-1">可恢复 <a class="header-anchor" href="#可恢复" aria-label="Permalink to &quot;可恢复&quot;">​</a></h3><p><strong>原则</strong>：提供明确的补救路径</p><table tabindex="0"><thead><tr><th>问题</th><th>补救路径</th></tr></thead><tbody><tr><td>忘记图案</td><td>通过助记词重置</td></tr><tr><td>交易失败</td><td>显示原因，提供重试</td></tr><tr><td>操作中断</td><td>保存草稿，下次继续</td></tr></tbody></table><hr><h2 id="_6-4-一致性体验" tabindex="-1">6.4 一致性体验 <a class="header-anchor" href="#_6-4-一致性体验" aria-label="Permalink to &quot;6.4 一致性体验&quot;">​</a></h2><h3 id="跨链统一" tabindex="-1">跨链统一 <a class="header-anchor" href="#跨链统一" aria-label="Permalink to &quot;跨链统一&quot;">​</a></h3><p><strong>原则</strong>：不同链的操作流程保持一致</p><table tabindex="0"><thead><tr><th>操作</th><th>ETH</th><th>TRX</th><th>BFMeta</th></tr></thead><tbody><tr><td>转账</td><td>相同流程</td><td>相同流程</td><td>相同流程</td></tr><tr><td>手续费</td><td>&quot;网络费用&quot;</td><td>&quot;网络费用&quot;</td><td>&quot;网络费用&quot;</td></tr><tr><td>地址格式</td><td>自动识别</td><td>自动识别</td><td>自动识别</td></tr></tbody></table><h3 id="视觉统一" tabindex="-1">视觉统一 <a class="header-anchor" href="#视觉统一" aria-label="Permalink to &quot;视觉统一&quot;">​</a></h3><p><strong>原则</strong>：相同功能使用相同的图标和位置</p><table tabindex="0"><thead><tr><th>功能</th><th>图标</th><th>位置</th></tr></thead><tbody><tr><td>返回</td><td>←</td><td>左上角</td></tr><tr><td>更多</td><td>⋯</td><td>右上角</td></tr><tr><td>复制</td><td>📋</td><td>地址后方</td></tr><tr><td>扫码</td><td>📷</td><td>输入框内右侧</td></tr></tbody></table><h3 id="语言统一" tabindex="-1">语言统一 <a class="header-anchor" href="#语言统一" aria-label="Permalink to &quot;语言统一&quot;">​</a></h3><p><strong>原则</strong>：用通俗语言解释技术概念</p><table tabindex="0"><thead><tr><th>技术术语</th><th>用户友好表述</th></tr></thead><tbody><tr><td>Gas Fee</td><td>网络费用</td></tr><tr><td>Nonce</td><td>(隐藏，不展示)</td></tr><tr><td>Block Confirmation</td><td>交易确认中</td></tr><tr><td>Private Key</td><td>私钥</td></tr><tr><td>Mnemonic</td><td>助记词</td></tr></tbody></table><hr><h2 id="_6-5-自适应布局" tabindex="-1">6.5 自适应布局 <a class="header-anchor" href="#_6-5-自适应布局" aria-label="Permalink to &quot;6.5 自适应布局&quot;">​</a></h2><h3 id="设计目标" tabindex="-1">设计目标 <a class="header-anchor" href="#设计目标" aria-label="Permalink to &quot;设计目标&quot;">​</a></h3><ul><li><strong>一份代码</strong>：无需为不同设备开发独立版本</li><li><strong>内容优先</strong>：根据可用空间展示合适的信息量</li><li><strong>交互一致</strong>：核心操作在各设备上保持相同</li></ul><h3 id="布局策略" tabindex="-1">布局策略 <a class="header-anchor" href="#布局策略" aria-label="Permalink to &quot;布局策略&quot;">​</a></h3><table tabindex="0"><thead><tr><th>设备</th><th>宽度</th><th>布局特点</th></tr></thead><tbody><tr><td>小型手机</td><td>&lt; 360px</td><td>紧凑单列，核心信息优先</td></tr><tr><td>标准手机</td><td>360-414px</td><td>标准单列，完整功能</td></tr><tr><td>大屏手机</td><td>414-600px</td><td>宽松单列，显示更多细节</td></tr><tr><td>折叠屏</td><td>600-840px</td><td>卡片并排</td></tr><tr><td>平板</td><td>&gt; 840px</td><td>分栏布局</td></tr></tbody></table><h3 id="典型场景" tabindex="-1">典型场景 <a class="header-anchor" href="#典型场景" aria-label="Permalink to &quot;典型场景&quot;">​</a></h3><p><strong>钱包卡片</strong></p><ul><li>窄屏：垂直排列信息</li><li>宽屏：水平展开，显示更多</li></ul><p><strong>转账确认</strong></p><ul><li>窄屏：全屏弹窗</li><li>宽屏：居中卡片式弹窗</li></ul><p><strong>设置页面</strong></p><ul><li>窄屏：单列列表</li><li>宽屏：分组卡片布局</li></ul><hr><h2 id="_6-6-无障碍设计" tabindex="-1">6.6 无障碍设计 <a class="header-anchor" href="#_6-6-无障碍设计" aria-label="Permalink to &quot;6.6 无障碍设计&quot;">​</a></h2><h3 id="色彩对比度" tabindex="-1">色彩对比度 <a class="header-anchor" href="#色彩对比度" aria-label="Permalink to &quot;色彩对比度&quot;">​</a></h3><ul><li>正文与背景对比度 ≥ 4.5:1</li><li>大文字与背景对比度 ≥ 3:1</li><li>不仅依赖颜色传达信息</li></ul><h3 id="触控目标" tabindex="-1">触控目标 <a class="header-anchor" href="#触控目标" aria-label="Permalink to &quot;触控目标&quot;">​</a></h3><ul><li>最小触控区域 44x44px</li><li>相邻可点击元素间距 ≥ 8px</li></ul><h3 id="屏幕阅读器" tabindex="-1">屏幕阅读器 <a class="header-anchor" href="#屏幕阅读器" aria-label="Permalink to &quot;屏幕阅读器&quot;">​</a></h3><ul><li>所有交互元素有 <code>aria-label</code></li><li>图标按钮有文字说明</li><li>表单字段关联 label</li></ul><h3 id="键盘导航" tabindex="-1">键盘导航 <a class="header-anchor" href="#键盘导航" aria-label="Permalink to &quot;键盘导航&quot;">​</a></h3><ul><li>Tab 顺序合理</li><li>焦点状态可见</li><li>支持 Enter/Space 激活</li></ul><hr><h2 id="_6-7-设计检查清单" tabindex="-1">6.7 设计检查清单 <a class="header-anchor" href="#_6-7-设计检查清单" aria-label="Permalink to &quot;6.7 设计检查清单&quot;">​</a></h2><p>开发新功能时，检查以下项目：</p><h3 id="信任" tabindex="-1">信任 <a class="header-anchor" href="#信任" aria-label="Permalink to &quot;信任&quot;">​</a></h3><ul><li>[ ] 是否清晰展示了所有关键信息？</li><li>[ ] 用户是否可以随时取消/返回？</li><li>[ ] 敏感操作是否有适当警告？</li></ul><h3 id="复杂度" tabindex="-1">复杂度 <a class="header-anchor" href="#复杂度" aria-label="Permalink to &quot;复杂度&quot;">​</a></h3><ul><li>[ ] 新手用户能否轻松完成？</li><li>[ ] 高级功能是否不干扰基础体验？</li><li>[ ] 是否提供了快捷操作？</li></ul><h3 id="错误预防" tabindex="-1">错误预防 <a class="header-anchor" href="#错误预防" aria-label="Permalink to &quot;错误预防&quot;">​</a></h3><ul><li>[ ] 是否有实时输入验证？</li><li>[ ] 是否有必要的二次确认？</li><li>[ ] 出错后是否有补救路径？</li></ul><h3 id="一致性" tabindex="-1">一致性 <a class="header-anchor" href="#一致性" aria-label="Permalink to &quot;一致性&quot;">​</a></h3><ul><li>[ ] 是否与其他类似功能保持一致？</li><li>[ ] 是否使用了正确的图标和位置？</li><li>[ ] 是否使用了用户友好的语言？</li></ul><h3 id="响应式" tabindex="-1">响应式 <a class="header-anchor" href="#响应式" aria-label="Permalink to &quot;响应式&quot;">​</a></h3><ul><li>[ ] 是否在不同屏幕尺寸下正常显示？</li><li>[ ] 是否使用了容器查询？</li><li>[ ] 核心功能是否始终可用？</li></ul><h3 id="无障碍" tabindex="-1">无障碍 <a class="header-anchor" href="#无障碍" aria-label="Permalink to &quot;无障碍&quot;">​</a></h3><ul><li>[ ] 色彩对比度是否足够？</li><li>[ ] 触控目标是否足够大？</li><li>[ ] 是否支持屏幕阅读器？</li></ul><hr><h2 id="本章小结" tabindex="-1">本章小结 <a class="header-anchor" href="#本章小结" aria-label="Permalink to &quot;本章小结&quot;">​</a></h2><ul><li>信任优先：透明、可控、安全提示</li><li>渐进复杂度：新手友好、进阶可选、专家高效</li><li>错误预防：格式验证、二次确认、可恢复</li><li>一致性：跨链统一、视觉统一、语言统一</li><li>自适应：一份代码适配多种设备</li><li>无障碍：色彩、触控、阅读器、键盘</li></ul><hr><h2 id="下一篇" tabindex="-1">下一篇 <a class="header-anchor" href="#下一篇" aria-label="Permalink to &quot;下一篇&quot;">​</a></h2><p>完成设计篇后，继续阅读 <a href="./../../03-架构篇/">第三篇：架构篇</a>，了解技术实现方案。</p>`,100)])])}const u=t(s,[["render",i]]);export{b as __pageData,u as default};
