import{_ as a,c as s,o as n,ag as e}from"./chunks/framework.B0we9iV-.js";const b=JSON.parse('{"title":"第十七章：交互规范","description":"","frontmatter":{},"headers":[],"relativePath":"white-book/05-组件篇/06-交互规范/index.md","filePath":"white-book/05-组件篇/06-交互规范/index.md"}'),d={name:"white-book/05-组件篇/06-交互规范/index.md"};function l(r,t,i,o,h,p){return n(),s("div",null,[...t[0]||(t[0]=[e(`<h1 id="第十七章-交互规范" tabindex="-1">第十七章：交互规范 <a class="header-anchor" href="#第十七章-交互规范" aria-label="Permalink to &quot;第十七章：交互规范&quot;">​</a></h1><blockquote><p>定义组件的交互行为、动效和可访问性规范</p></blockquote><hr><h2 id="_17-1-触摸交互规范" tabindex="-1">17.1 触摸交互规范 <a class="header-anchor" href="#_17-1-触摸交互规范" aria-label="Permalink to &quot;17.1 触摸交互规范&quot;">​</a></h2><h3 id="触摸目标" tabindex="-1">触摸目标 <a class="header-anchor" href="#触摸目标" aria-label="Permalink to &quot;触摸目标&quot;">​</a></h3><table tabindex="0"><thead><tr><th>规范</th><th>要求</th></tr></thead><tbody><tr><td>最小尺寸</td><td>44 × 44 px（iOS）/ 48 × 48 dp（Android）</td></tr><tr><td>间距</td><td>相邻可触摸元素间距 ≥ 8 px</td></tr><tr><td>热区扩展</td><td>视觉元素小于最小尺寸时，扩展触摸热区</td></tr></tbody></table><h3 id="触摸反馈" tabindex="-1">触摸反馈 <a class="header-anchor" href="#触摸反馈" aria-label="Permalink to &quot;触摸反馈&quot;">​</a></h3><table tabindex="0"><thead><tr><th>事件</th><th>反馈</th><th>时机</th></tr></thead><tbody><tr><td>touch start</td><td>视觉变化（透明度/缩放/颜色）</td><td>立即</td></tr><tr><td>touch end</td><td>恢复原状</td><td>立即</td></tr><tr><td>long press</td><td>振动 + 上下文菜单</td><td>500ms 后</td></tr></tbody></table><p><strong>按压态规范</strong>：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Button：</span></span>
<span class="line"><span>  - 背景色变深 10% 或</span></span>
<span class="line"><span>  - 透明度降至 0.7 或</span></span>
<span class="line"><span>  - 缩放至 0.98</span></span>
<span class="line"><span></span></span>
<span class="line"><span>Card (pressable)：</span></span>
<span class="line"><span>  - 缩放至 0.98 或</span></span>
<span class="line"><span>  - 阴影增强</span></span></code></pre></div><hr><h2 id="_17-2-手势规范" tabindex="-1">17.2 手势规范 <a class="header-anchor" href="#_17-2-手势规范" aria-label="Permalink to &quot;17.2 手势规范&quot;">​</a></h2><h3 id="滑动手势" tabindex="-1">滑动手势 <a class="header-anchor" href="#滑动手势" aria-label="Permalink to &quot;滑动手势&quot;">​</a></h3><table tabindex="0"><thead><tr><th>手势</th><th>方向</th><th>应用场景</th></tr></thead><tbody><tr><td>滑动</td><td>水平</td><td>列表项滑动删除/操作</td></tr><tr><td>滑动</td><td>垂直</td><td>页面滚动、Sheet 关闭</td></tr><tr><td>下拉</td><td>垂直向下</td><td>刷新内容</td></tr></tbody></table><p><strong>Sheet 关闭手势</strong>：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>下滑距离 &lt; 30% 高度 → 弹回打开状态</span></span>
<span class="line"><span>下滑距离 ≥ 30% 高度 → 关闭 Sheet</span></span>
<span class="line"><span>下滑速度 &gt; 阈值 → 无论距离，直接关闭</span></span></code></pre></div><h3 id="缩放-旋转" tabindex="-1">缩放/旋转 <a class="header-anchor" href="#缩放-旋转" aria-label="Permalink to &quot;缩放/旋转&quot;">​</a></h3><ul><li><strong>MAY</strong> 图片查看器支持双指缩放</li><li><strong>MUST NOT</strong> 在普通页面拦截系统缩放</li></ul><hr><h2 id="_17-3-动效规范" tabindex="-1">17.3 动效规范 <a class="header-anchor" href="#_17-3-动效规范" aria-label="Permalink to &quot;17.3 动效规范&quot;">​</a></h2><h3 id="时间曲线" tabindex="-1">时间曲线 <a class="header-anchor" href="#时间曲线" aria-label="Permalink to &quot;时间曲线&quot;">​</a></h3><table tabindex="0"><thead><tr><th>动效类型</th><th>持续时间</th><th>缓动函数</th></tr></thead><tbody><tr><td>微交互（按钮反馈）</td><td>100-150ms</td><td>ease-out</td></tr><tr><td>页面切换</td><td>250-350ms</td><td>ease-in-out</td></tr><tr><td>Sheet 展开/收起</td><td>300-400ms</td><td>spring (damping: 0.8)</td></tr><tr><td>加载骨架闪烁</td><td>1500ms</td><td>linear (循环)</td></tr></tbody></table><h3 id="页面转场" tabindex="-1">页面转场 <a class="header-anchor" href="#页面转场" aria-label="Permalink to &quot;页面转场&quot;">​</a></h3><p><strong>Push 进入</strong>：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>新页面：从右侧 100% 滑入，透明度 1</span></span>
<span class="line"><span>旧页面：向左偏移 30%，透明度降至 0.3</span></span></code></pre></div><p><strong>Pop 返回</strong>：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>当前页面：向右滑出 100%，透明度 1</span></span>
<span class="line"><span>上一页面：从左侧 30% 偏移恢复，透明度恢复至 1</span></span></code></pre></div><h3 id="减少动效模式" tabindex="-1">减少动效模式 <a class="header-anchor" href="#减少动效模式" aria-label="Permalink to &quot;减少动效模式&quot;">​</a></h3><ul><li><strong>MUST</strong> 检测系统&quot;减少动效&quot;设置</li><li><strong>MUST</strong> 减少动效模式下： <ul><li>禁用弹簧动画</li><li>使用淡入淡出替代滑动</li><li>缩短动画时间至最小</li></ul></li></ul><hr><h2 id="_17-4-加载状态规范" tabindex="-1">17.4 加载状态规范 <a class="header-anchor" href="#_17-4-加载状态规范" aria-label="Permalink to &quot;17.4 加载状态规范&quot;">​</a></h2><h3 id="骨架屏" tabindex="-1">骨架屏 <a class="header-anchor" href="#骨架屏" aria-label="Permalink to &quot;骨架屏&quot;">​</a></h3><table tabindex="0"><thead><tr><th>适用场景</th><th>形态</th></tr></thead><tbody><tr><td>文本</td><td>圆角矩形，高度与文字行高一致</td></tr><tr><td>头像</td><td>圆形</td></tr><tr><td>卡片</td><td>与实际卡片尺寸一致的圆角矩形</td></tr><tr><td>列表</td><td>重复 3-5 个骨架项</td></tr></tbody></table><p><strong>动画</strong>：</p><ul><li>渐变色从左向右扫过</li><li>周期：1.5-2s</li><li>不透明度：0.1 → 0.3 → 0.1</li></ul><h3 id="加载指示器" tabindex="-1">加载指示器 <a class="header-anchor" href="#加载指示器" aria-label="Permalink to &quot;加载指示器&quot;">​</a></h3><table tabindex="0"><thead><tr><th>类型</th><th>适用场景</th></tr></thead><tbody><tr><td>Spinner</td><td>按钮内、小区域加载</td></tr><tr><td>Skeleton</td><td>页面/组件首次加载</td></tr><tr><td>Progress</td><td>已知进度的操作</td></tr></tbody></table><h3 id="空状态" tabindex="-1">空状态 <a class="header-anchor" href="#空状态" aria-label="Permalink to &quot;空状态&quot;">​</a></h3><p><strong>结构</strong>：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>┌─────────────────────────────────┐</span></span>
<span class="line"><span>│          [插图/图标]             │</span></span>
<span class="line"><span>│                                 │</span></span>
<span class="line"><span>│         主标题                   │</span></span>
<span class="line"><span>│         副标题（可选）           │</span></span>
<span class="line"><span>│                                 │</span></span>
<span class="line"><span>│         [操作按钮]               │</span></span>
<span class="line"><span>└─────────────────────────────────┘</span></span></code></pre></div><p><strong>文案规范</strong>：</p><ul><li>主标题：描述当前状态（如&quot;暂无交易记录&quot;）</li><li>副标题：说明原因或引导操作（如&quot;完成首笔转账后将在此显示&quot;）</li></ul><hr><h2 id="_17-5-错误状态规范" tabindex="-1">17.5 错误状态规范 <a class="header-anchor" href="#_17-5-错误状态规范" aria-label="Permalink to &quot;17.5 错误状态规范&quot;">​</a></h2><h3 id="网络错误" tabindex="-1">网络错误 <a class="header-anchor" href="#网络错误" aria-label="Permalink to &quot;网络错误&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>┌─────────────────────────────────┐</span></span>
<span class="line"><span>│         [断网图标]               │</span></span>
<span class="line"><span>│                                 │</span></span>
<span class="line"><span>│         网络连接失败             │</span></span>
<span class="line"><span>│         请检查网络设置           │</span></span>
<span class="line"><span>│                                 │</span></span>
<span class="line"><span>│         [重试]                   │</span></span>
<span class="line"><span>└─────────────────────────────────┘</span></span></code></pre></div><h3 id="服务器错误" tabindex="-1">服务器错误 <a class="header-anchor" href="#服务器错误" aria-label="Permalink to &quot;服务器错误&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>┌─────────────────────────────────┐</span></span>
<span class="line"><span>│         [错误图标]               │</span></span>
<span class="line"><span>│                                 │</span></span>
<span class="line"><span>│         服务暂时不可用           │</span></span>
<span class="line"><span>│         请稍后再试               │</span></span>
<span class="line"><span>│                                 │</span></span>
<span class="line"><span>│         [重试]                   │</span></span>
<span class="line"><span>└─────────────────────────────────┘</span></span></code></pre></div><h3 id="操作失败" tabindex="-1">操作失败 <a class="header-anchor" href="#操作失败" aria-label="Permalink to &quot;操作失败&quot;">​</a></h3><p>使用 Toast 或 Alert：</p><ul><li><strong>Toast</strong>：轻量提示，3-5 秒自动消失</li><li><strong>Alert</strong>：需要用户确认的重要错误</li></ul><hr><h2 id="_17-6-反馈规范" tabindex="-1">17.6 反馈规范 <a class="header-anchor" href="#_17-6-反馈规范" aria-label="Permalink to &quot;17.6 反馈规范&quot;">​</a></h2><h3 id="toast-消息" tabindex="-1">Toast 消息 <a class="header-anchor" href="#toast-消息" aria-label="Permalink to &quot;Toast 消息&quot;">​</a></h3><table tabindex="0"><thead><tr><th>类型</th><th>图标</th><th>持续时间</th></tr></thead><tbody><tr><td>success</td><td>✓</td><td>2s</td></tr><tr><td>error</td><td>✗</td><td>3s</td></tr><tr><td>warning</td><td>⚠</td><td>3s</td></tr><tr><td>info</td><td>ℹ</td><td>2s</td></tr></tbody></table><p><strong>位置</strong>：屏幕顶部或底部居中</p><p><strong>行为</strong>：</p><ul><li>可堆叠显示</li><li>向上/向下滑动可提前关闭</li><li>同时最多显示 3 个</li></ul><h3 id="振动反馈" tabindex="-1">振动反馈 <a class="header-anchor" href="#振动反馈" aria-label="Permalink to &quot;振动反馈&quot;">​</a></h3><table tabindex="0"><thead><tr><th>场景</th><th>振动类型</th></tr></thead><tbody><tr><td>按钮点击</td><td>轻触（light）</td></tr><tr><td>操作成功</td><td>成功（success）</td></tr><tr><td>操作失败</td><td>错误（error）</td></tr><tr><td>危险操作确认</td><td>警告（warning）</td></tr></tbody></table><p><strong>规范</strong>：</p><ul><li><strong>MUST</strong> 检测系统振动设置</li><li><strong>MUST NOT</strong> 在静音模式下振动（除非用户明确开启）</li></ul><hr><h2 id="_17-7-可访问性规范" tabindex="-1">17.7 可访问性规范 <a class="header-anchor" href="#_17-7-可访问性规范" aria-label="Permalink to &quot;17.7 可访问性规范&quot;">​</a></h2><h3 id="语义结构" tabindex="-1">语义结构 <a class="header-anchor" href="#语义结构" aria-label="Permalink to &quot;语义结构&quot;">​</a></h3><table tabindex="0"><thead><tr><th>元素</th><th>语义角色</th></tr></thead><tbody><tr><td>导航栏</td><td>role=&quot;navigation&quot;</td></tr><tr><td>主内容</td><td>role=&quot;main&quot;</td></tr><tr><td>对话框</td><td>role=&quot;dialog&quot;</td></tr><tr><td>列表</td><td>role=&quot;list&quot; + role=&quot;listitem&quot;</td></tr><tr><td>按钮</td><td>role=&quot;button&quot;</td></tr></tbody></table><h3 id="焦点管理" tabindex="-1">焦点管理 <a class="header-anchor" href="#焦点管理" aria-label="Permalink to &quot;焦点管理&quot;">​</a></h3><p><strong>焦点顺序</strong>：</p><ul><li><strong>MUST</strong> 遵循视觉阅读顺序（从上到下，从左到右）</li><li><strong>MUST</strong> 可通过键盘 Tab 遍历所有可交互元素</li></ul><p><strong>焦点陷阱</strong>：</p><ul><li><strong>MUST</strong> 模态对话框内焦点循环</li><li><strong>MUST</strong> 关闭对话框时焦点返回触发元素</li></ul><h3 id="屏幕阅读器" tabindex="-1">屏幕阅读器 <a class="header-anchor" href="#屏幕阅读器" aria-label="Permalink to &quot;屏幕阅读器&quot;">​</a></h3><p><strong>必需的 accessible name</strong>：</p><ul><li>所有按钮</li><li>所有输入框</li><li>所有图标按钮（使用 aria-label）</li></ul><p><strong>动态内容</strong>：</p><ul><li>使用 aria-live 通知重要变化</li><li>polite：非紧急更新</li><li>assertive：错误信息</li></ul><h3 id="对比度" tabindex="-1">对比度 <a class="header-anchor" href="#对比度" aria-label="Permalink to &quot;对比度&quot;">​</a></h3><table tabindex="0"><thead><tr><th>元素</th><th>最小对比度</th></tr></thead><tbody><tr><td>正文文本</td><td>4.5:1</td></tr><tr><td>大文本（≥18px）</td><td>3:1</td></tr><tr><td>图标</td><td>3:1</td></tr><tr><td>禁用状态</td><td>无要求</td></tr></tbody></table><hr><h2 id="_17-8-响应式规范" tabindex="-1">17.8 响应式规范 <a class="header-anchor" href="#_17-8-响应式规范" aria-label="Permalink to &quot;17.8 响应式规范&quot;">​</a></h2><h3 id="断点" tabindex="-1">断点 <a class="header-anchor" href="#断点" aria-label="Permalink to &quot;断点&quot;">​</a></h3><table tabindex="0"><thead><tr><th>断点</th><th>宽度范围</th><th>典型设备</th></tr></thead><tbody><tr><td>xs</td><td>&lt; 360px</td><td>小屏手机</td></tr><tr><td>sm</td><td>360-428px</td><td>普通手机（设计基准）</td></tr><tr><td>md</td><td>428-768px</td><td>大屏手机/小平板</td></tr><tr><td>lg</td><td>≥ 768px</td><td>平板（可选支持）</td></tr></tbody></table><h3 id="适配策略" tabindex="-1">适配策略 <a class="header-anchor" href="#适配策略" aria-label="Permalink to &quot;适配策略&quot;">​</a></h3><p><strong>文字</strong>：</p><ul><li>使用相对单位（rem/em）</li><li>标题：1.25-1.5rem</li><li>正文：1rem</li><li>辅助文字：0.875rem</li></ul><p><strong>布局</strong>：</p><ul><li>使用弹性布局（flexbox）</li><li>卡片网格：1 列（sm） / 2 列（md+）</li><li>水平间距：16-24px</li><li>垂直间距：8-16px</li></ul><hr><h2 id="本章小结" tabindex="-1">本章小结 <a class="header-anchor" href="#本章小结" aria-label="Permalink to &quot;本章小结&quot;">​</a></h2><ul><li>触摸目标最小 44×44px，有明确触摸反馈</li><li>手势行为遵循平台规范</li><li>动效时间和曲线有统一标准</li><li>加载、空、错误状态都有规范模板</li><li>可访问性是必需项，不是可选项</li></ul><hr><h2 id="下一篇" tabindex="-1">下一篇 <a class="header-anchor" href="#下一篇" aria-label="Permalink to &quot;下一篇&quot;">​</a></h2><p>完成组件篇后，继续阅读 <a href="./../../06-安全篇/">第六篇：安全篇</a>。</p>`,93)])])}const u=a(d,[["render",l]]);export{b as __pageData,u as default};
