import{_ as t,c as o,o as a,ag as r}from"./chunks/framework.B0we9iV-.js";const f=JSON.parse('{"title":"BioForest SDK Integration","description":"","frontmatter":{},"headers":[],"relativePath":"white-book/06-Service-Ref/05-BioForest-SDK/01-Core-Integration.md","filePath":"white-book/06-Service-Ref/05-BioForest-SDK/01-Core-Integration.md"}'),i={name:"white-book/06-Service-Ref/05-BioForest-SDK/01-Core-Integration.md"};function n(s,e,c,l,d,h){return a(),o("div",null,[...e[0]||(e[0]=[r('<h1 id="bioforest-sdk-integration" tabindex="-1">BioForest SDK Integration <a class="header-anchor" href="#bioforest-sdk-integration" aria-label="Permalink to &quot;BioForest SDK Integration&quot;">​</a></h1><blockquote><p><strong>Code Source</strong>: <a href="https://github.com/BioforestChain/KeyApp/blob/main/src/services/bioforest-sdk/index.ts" target="_blank" rel="noreferrer"><code>src/services/bioforest-sdk/index.ts</code></a></p></blockquote><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>This module serves as the <strong>exclusive</strong> bridge to the low-level <code>bfchain-core</code> library. It manages the dynamic loading of the heavy SDK bundle and provides high-level helpers for transaction operations.</p><h2 id="architecture" tabindex="-1">Architecture <a class="header-anchor" href="#architecture" aria-label="Permalink to &quot;Architecture&quot;">​</a></h2><ul><li><strong>Lazy Loading</strong>: The core bundle (<code>bioforest-chain-bundle.js</code>) is imported only when needed to optimize startup time.</li><li><strong>Genesis Management</strong>: Fetches and caches genesis blocks from <code>/configs/genesis/{chainId}.json</code>.</li><li><strong>Core Isolation</strong>: Creates isolated <code>BFChainCore</code> instances for each chain to prevent state pollution.</li></ul><h2 id="key-functions" tabindex="-1">Key Functions <a class="header-anchor" href="#key-functions" aria-label="Permalink to &quot;Key Functions&quot;">​</a></h2><h3 id="createtransfertransaction" tabindex="-1"><code>createTransferTransaction</code> <a class="header-anchor" href="#createtransfertransaction" aria-label="Permalink to &quot;`createTransferTransaction`&quot;">​</a></h3><p>Constructs a native asset transfer transaction.</p><ul><li>automatically fetches the latest block height (<code>applyBlockHeight</code>).</li><li>calculates minimum fees via <code>getTransferTransactionMinFee</code>.</li><li>handles &quot;Second Secret&quot; (Payment Password) logic (v1/v2 compatibility).</li></ul><h3 id="broadcasttransaction" tabindex="-1"><code>broadcastTransaction</code> <a class="header-anchor" href="#broadcasttransaction" aria-label="Permalink to &quot;`broadcastTransaction`&quot;">​</a></h3><p>Submits a signed transaction to the network via the configured RPC endpoint.</p><ul><li><strong>Error Handling</strong>: Parses chain-specific error codes (e.g., <code>minFee</code> requirements).</li></ul><h3 id="verifytwostepsecret" tabindex="-1"><code>verifyTwoStepSecret</code> <a class="header-anchor" href="#verifytwostepsecret" aria-label="Permalink to &quot;`verifyTwoStepSecret`&quot;">​</a></h3><p>Validates the user&#39;s payment password against the stored second public key.</p><ul><li>Supports both legacy (v1) and modern (v2) key derivation algorithms.</li></ul>',16)])])}const p=t(i,[["render",n]]);export{f as __pageData,p as default};
