import{_ as a,c as n,o as s,ag as e}from"./chunks/framework.B0we9iV-.js";const u=JSON.parse('{"title":"多语言支持","description":"","frontmatter":{},"headers":[],"relativePath":"white-book/07-国际化篇/01-多语言支持/index.md","filePath":"white-book/07-国际化篇/01-多语言支持/index.md"}'),l={name:"white-book/07-国际化篇/01-多语言支持/index.md"};function i(d,t,r,o,h,p){return s(),n("div",null,[...t[0]||(t[0]=[e(`<h1 id="多语言支持" tabindex="-1">多语言支持 <a class="header-anchor" href="#多语言支持" aria-label="Permalink to &quot;多语言支持&quot;">​</a></h1><blockquote><p>定义国际化架构规范</p></blockquote><hr><h2 id="支持的语言" tabindex="-1">支持的语言 <a class="header-anchor" href="#支持的语言" aria-label="Permalink to &quot;支持的语言&quot;">​</a></h2><table tabindex="0"><thead><tr><th>语言代码</th><th>语言名称</th><th>书写方向</th></tr></thead><tbody><tr><td>zh-CN</td><td>简体中文</td><td>LTR</td></tr><tr><td>zh-TW</td><td>繁體中文</td><td>LTR</td></tr><tr><td>en</td><td>English</td><td>LTR</td></tr><tr><td>ja</td><td>日本語</td><td>LTR</td></tr><tr><td>ko</td><td>한국어</td><td>LTR</td></tr><tr><td>ar</td><td>العربية</td><td>RTL</td></tr></tbody></table><hr><h2 id="语言检测规范" tabindex="-1">语言检测规范 <a class="header-anchor" href="#语言检测规范" aria-label="Permalink to &quot;语言检测规范&quot;">​</a></h2><h3 id="检测顺序" tabindex="-1">检测顺序 <a class="header-anchor" href="#检测顺序" aria-label="Permalink to &quot;检测顺序&quot;">​</a></h3><ol><li><strong>本地存储</strong> - 用户上次选择的语言</li><li><strong>浏览器语言</strong> - navigator.language</li><li><strong>系统语言</strong> - 操作系统语言设置</li><li><strong>默认语言</strong> - 英语 (en)</li></ol><h3 id="语言匹配规则" tabindex="-1">语言匹配规则 <a class="header-anchor" href="#语言匹配规则" aria-label="Permalink to &quot;语言匹配规则&quot;">​</a></h3><ul><li><strong>MUST</strong> 精确匹配优先（zh-CN → zh-CN）</li><li><strong>SHOULD</strong> 支持语言族匹配（zh-HK → zh-TW）</li><li><strong>MUST</strong> 无匹配时使用默认语言</li></ul><h3 id="语言族映射" tabindex="-1">语言族映射 <a class="header-anchor" href="#语言族映射" aria-label="Permalink to &quot;语言族映射&quot;">​</a></h3><table tabindex="0"><thead><tr><th>请求语言</th><th>匹配结果</th></tr></thead><tbody><tr><td>zh-CN</td><td>zh-CN</td></tr><tr><td>zh-TW</td><td>zh-TW</td></tr><tr><td>zh-HK</td><td>zh-TW</td></tr><tr><td>zh</td><td>zh-CN</td></tr><tr><td>en-US</td><td>en</td></tr><tr><td>en-GB</td><td>en</td></tr></tbody></table><hr><h2 id="翻译资源规范" tabindex="-1">翻译资源规范 <a class="header-anchor" href="#翻译资源规范" aria-label="Permalink to &quot;翻译资源规范&quot;">​</a></h2><h3 id="命名空间划分" tabindex="-1">命名空间划分 <a class="header-anchor" href="#命名空间划分" aria-label="Permalink to &quot;命名空间划分&quot;">​</a></h3><table tabindex="0"><thead><tr><th>命名空间</th><th>用途</th></tr></thead><tbody><tr><td>common</td><td>通用词汇（确认、取消、返回等）</td></tr><tr><td>wallet</td><td>钱包相关</td></tr><tr><td>transfer</td><td>转账相关</td></tr><tr><td>settings</td><td>设置相关</td></tr><tr><td>error</td><td>错误消息</td></tr><tr><td>auth</td><td>认证相关</td></tr></tbody></table><h3 id="翻译键命名规范" tabindex="-1">翻译键命名规范 <a class="header-anchor" href="#翻译键命名规范" aria-label="Permalink to &quot;翻译键命名规范&quot;">​</a></h3><ul><li><strong>MUST</strong> 使用小驼峰命名</li><li><strong>MUST</strong> 语义化命名</li><li><strong>SHOULD</strong> 按功能模块组织</li><li><strong>SHOULD</strong> 避免超过 3 层嵌套</li></ul><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>示例：</span></span>
<span class="line"><span>✓ createWallet</span></span>
<span class="line"><span>✓ balance.available</span></span>
<span class="line"><span>✓ error.networkTimeout</span></span>
<span class="line"><span>✗ btn_create_wallet</span></span>
<span class="line"><span>✗ a.b.c.d.e.f</span></span></code></pre></div><h3 id="翻译文本规范" tabindex="-1">翻译文本规范 <a class="header-anchor" href="#翻译文本规范" aria-label="Permalink to &quot;翻译文本规范&quot;">​</a></h3><ul><li><strong>MUST</strong> 避免拼接翻译（使用插值）</li><li><strong>MUST</strong> 考虑不同语言的语序差异</li><li><strong>SHOULD</strong> 为变量提供语义化的占位符名</li><li><strong>SHOULD</strong> 添加翻译备注说明上下文</li></ul><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>✓ &quot;available&quot;: &quot;可用: {{amount}} {{symbol}}&quot;</span></span>
<span class="line"><span>✗ &quot;available&quot;: &quot;可用: &quot; + amount + &quot; &quot; + symbol</span></span></code></pre></div><hr><h2 id="插值规范" tabindex="-1">插值规范 <a class="header-anchor" href="#插值规范" aria-label="Permalink to &quot;插值规范&quot;">​</a></h2><h3 id="基本插值" tabindex="-1">基本插值 <a class="header-anchor" href="#基本插值" aria-label="Permalink to &quot;基本插值&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>翻译文本: &quot;余额: {{amount}}&quot;</span></span>
<span class="line"><span>调用方式: t(&#39;balance&#39;, { amount: &#39;100&#39; })</span></span>
<span class="line"><span>结果: &quot;余额: 100&quot;</span></span></code></pre></div><h3 id="复数处理" tabindex="-1">复数处理 <a class="header-anchor" href="#复数处理" aria-label="Permalink to &quot;复数处理&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>翻译文本:</span></span>
<span class="line"><span>  &quot;items_zero&quot;: &quot;无项目&quot;</span></span>
<span class="line"><span>  &quot;items_one&quot;: &quot;1 个项目&quot;</span></span>
<span class="line"><span>  &quot;items_other&quot;: &quot;{{count}} 个项目&quot;</span></span>
<span class="line"><span></span></span>
<span class="line"><span>调用方式: t(&#39;items&#39;, { count: 5 })</span></span></code></pre></div><h3 id="日期时间格式" tabindex="-1">日期时间格式 <a class="header-anchor" href="#日期时间格式" aria-label="Permalink to &quot;日期时间格式&quot;">​</a></h3><table tabindex="0"><thead><tr><th>格式</th><th>zh-CN</th><th>en</th></tr></thead><tbody><tr><td>短日期</td><td>2024/01/15</td><td>01/15/2024</td></tr><tr><td>长日期</td><td>2024年1月15日</td><td>January 15, 2024</td></tr><tr><td>时间</td><td>14:30</td><td>2:30 PM</td></tr></tbody></table><hr><h2 id="rtl-布局规范" tabindex="-1">RTL 布局规范 <a class="header-anchor" href="#rtl-布局规范" aria-label="Permalink to &quot;RTL 布局规范&quot;">​</a></h2><h3 id="css-逻辑属性" tabindex="-1">CSS 逻辑属性 <a class="header-anchor" href="#css-逻辑属性" aria-label="Permalink to &quot;CSS 逻辑属性&quot;">​</a></h3><table tabindex="0"><thead><tr><th>物理属性</th><th>逻辑属性</th></tr></thead><tbody><tr><td>padding-left</td><td>padding-inline-start</td></tr><tr><td>padding-right</td><td>padding-inline-end</td></tr><tr><td>margin-left</td><td>margin-inline-start</td></tr><tr><td>margin-right</td><td>margin-inline-end</td></tr><tr><td>left</td><td>inset-inline-start</td></tr><tr><td>right</td><td>inset-inline-end</td></tr><tr><td>text-align: left</td><td>text-align: start</td></tr><tr><td>text-align: right</td><td>text-align: end</td></tr></tbody></table><h3 id="rtl-规范要求" tabindex="-1">RTL 规范要求 <a class="header-anchor" href="#rtl-规范要求" aria-label="Permalink to &quot;RTL 规范要求&quot;">​</a></h3><ul><li><strong>MUST</strong> 使用 CSS 逻辑属性</li><li><strong>MUST</strong> 设置 <code>dir</code> 属性（html 或容器）</li><li><strong>MUST</strong> 设置 <code>lang</code> 属性</li><li><strong>SHOULD</strong> 翻转方向性图标（箭头等）</li><li><strong>MUST NOT</strong> 翻转非方向性图标（品牌 logo）</li></ul><h3 id="需要翻转的元素" tabindex="-1">需要翻转的元素 <a class="header-anchor" href="#需要翻转的元素" aria-label="Permalink to &quot;需要翻转的元素&quot;">​</a></h3><table tabindex="0"><thead><tr><th>元素</th><th>翻转</th><th>说明</th></tr></thead><tbody><tr><td>返回箭头</td><td>是</td><td>方向性</td></tr><tr><td>前进箭头</td><td>是</td><td>方向性</td></tr><tr><td>列表顺序</td><td>是</td><td>起始位置变化</td></tr><tr><td>进度条方向</td><td>是</td><td>从右到左</td></tr><tr><td>品牌 Logo</td><td>否</td><td>固定资产</td></tr><tr><td>电话号码</td><td>否</td><td>数字方向不变</td></tr></tbody></table><hr><h2 id="语言切换规范" tabindex="-1">语言切换规范 <a class="header-anchor" href="#语言切换规范" aria-label="Permalink to &quot;语言切换规范&quot;">​</a></h2><h3 id="切换流程" tabindex="-1">切换流程 <a class="header-anchor" href="#切换流程" aria-label="Permalink to &quot;切换流程&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>用户进入语言设置</span></span>
<span class="line"><span>    │</span></span>
<span class="line"><span>    ▼</span></span>
<span class="line"><span>显示语言列表（当前语言高亮）</span></span>
<span class="line"><span>    │</span></span>
<span class="line"><span>    ▼</span></span>
<span class="line"><span>用户选择新语言</span></span>
<span class="line"><span>    │</span></span>
<span class="line"><span>    ▼</span></span>
<span class="line"><span>保存到本地存储</span></span>
<span class="line"><span>    │</span></span>
<span class="line"><span>    ▼</span></span>
<span class="line"><span>更新应用语言</span></span>
<span class="line"><span>    │</span></span>
<span class="line"><span>    ▼</span></span>
<span class="line"><span>刷新界面文本</span></span>
<span class="line"><span>    │</span></span>
<span class="line"><span>    ▼</span></span>
<span class="line"><span>更新 HTML dir 和 lang 属性</span></span></code></pre></div><h3 id="切换要求" tabindex="-1">切换要求 <a class="header-anchor" href="#切换要求" aria-label="Permalink to &quot;切换要求&quot;">​</a></h3><ul><li><strong>MUST</strong> 立即生效（无需重启）</li><li><strong>MUST</strong> 持久化到本地存储</li><li><strong>MUST</strong> 更新所有可见文本</li><li><strong>SHOULD</strong> 显示本地语言名称（非翻译名）</li></ul><h3 id="语言列表显示" tabindex="-1">语言列表显示 <a class="header-anchor" href="#语言列表显示" aria-label="Permalink to &quot;语言列表显示&quot;">​</a></h3><p>每种语言 SHOULD 使用其本地名称显示：</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>简体中文</span></span>
<span class="line"><span>繁體中文</span></span>
<span class="line"><span>English</span></span>
<span class="line"><span>日本語</span></span>
<span class="line"><span>한국어</span></span>
<span class="line"><span>العربية</span></span></code></pre></div><hr><h2 id="开发规范" tabindex="-1">开发规范 <a class="header-anchor" href="#开发规范" aria-label="Permalink to &quot;开发规范&quot;">​</a></h2><h3 id="添加新翻译" tabindex="-1">添加新翻译 <a class="header-anchor" href="#添加新翻译" aria-label="Permalink to &quot;添加新翻译&quot;">​</a></h3><ol><li>在所有语言文件中添加新键</li><li>确保键名一致</li><li>添加必要的复数形式</li><li>测试所有语言显示</li></ol><h3 id="翻译缺失处理" tabindex="-1">翻译缺失处理 <a class="header-anchor" href="#翻译缺失处理" aria-label="Permalink to &quot;翻译缺失处理&quot;">​</a></h3><ul><li><strong>MUST</strong> 有回退语言机制</li><li><strong>SHOULD</strong> 开发环境显示缺失键警告</li><li><strong>SHOULD</strong> 生产环境显示回退语言文本</li><li><strong>MAY</strong> 上报缺失翻译</li></ul><h3 id="测试规范" tabindex="-1">测试规范 <a class="header-anchor" href="#测试规范" aria-label="Permalink to &quot;测试规范&quot;">​</a></h3><ul><li><strong>MUST</strong> 测试所有支持语言的界面</li><li><strong>MUST</strong> 测试 RTL 布局正确性</li><li><strong>SHOULD</strong> 测试长文本不溢出</li><li><strong>SHOULD</strong> 测试占位符替换正确</li></ul><hr><h2 id="本章小结" tabindex="-1">本章小结 <a class="header-anchor" href="#本章小结" aria-label="Permalink to &quot;本章小结&quot;">​</a></h2><ul><li>支持 6 种语言，包括 RTL 阿拉伯语</li><li>自动语言检测，支持用户手动切换</li><li>使用 CSS 逻辑属性支持双向布局</li><li>统一的翻译资源管理和命名规范</li><li>语言切换即时生效</li></ul>`,59)])])}const b=a(l,[["render",i]]);export{u as __pageData,b as default};
