import{_ as t,c as a,o as r,ag as i}from"./chunks/framework.B0we9iV-.js";const v=JSON.parse('{"title":"00. 生态架构 (Overview)","description":"","frontmatter":{},"headers":[],"relativePath":"white-book/11-DApp-Guide/00-Overview.md","filePath":"white-book/11-DApp-Guide/00-Overview.md"}'),o={name:"white-book/11-DApp-Guide/00-Overview.md"};function n(l,e,s,p,d,_){return r(),a("div",null,[...e[0]||(e[0]=[i('<h1 id="_00-生态架构-overview" tabindex="-1">00. 生态架构 (Overview) <a class="header-anchor" href="#_00-生态架构-overview" aria-label="Permalink to &quot;00. 生态架构 (Overview)&quot;">​</a></h1><p>KeyApp 旨在构建一个去中心化的 DApp 操作系统。我们不只是提供一个浏览器，而是提供一个<strong>运行时 (Runtime)</strong>。</p><h2 id="接入方式" tabindex="-1">接入方式 <a class="header-anchor" href="#接入方式" aria-label="Permalink to &quot;接入方式&quot;">​</a></h2><ol><li><strong>Miniapp (推荐)</strong>: 遵循 KeyApp Manifest 规范的 Web 应用。可以获得完整的 OS 能力（如多窗口、深色模式同步、原生通知）。</li><li><strong>Standard Web App</strong>: 普通的 H5 应用。在受限的浏览器容器中运行，可以使用标准的 Wallet 连接协议。</li></ol><h2 id="核心价值" tabindex="-1">核心价值 <a class="header-anchor" href="#核心价值" aria-label="Permalink to &quot;核心价值&quot;">​</a></h2><ul><li><strong>Discoverability</strong>: 通过去中心化的 App Store 分发。</li><li><strong>Native Experience</strong>: 像原生应用一样运行，而不是像网页一样运行。</li><li><strong>Security</strong>: 基于 Manifest 的权限声明系统，用户明确知道 DApp 请求了什么。</li></ul>',6)])])}const h=t(o,[["render",n]]);export{v as __pageData,h as default};
