import{_ as d,c as r,o,ag as a}from"./chunks/framework.B0we9iV-.js";const c=JSON.parse('{"title":"术语表","description":"","frontmatter":{},"headers":[],"relativePath":"white-book/99-Appendix/01-Glossary.md","filePath":"white-book/99-Appendix/01-Glossary.md"}'),e={name:"white-book/99-Appendix/01-Glossary.md"};function n(s,t,h,i,g,l){return o(),r("div",null,[...t[0]||(t[0]=[a('<h1 id="术语表" tabindex="-1">术语表 <a class="header-anchor" href="#术语表" aria-label="Permalink to &quot;术语表&quot;">​</a></h1><blockquote><p>技术术语和业务概念速查</p></blockquote><hr><h2 id="a-区块链与密码学" tabindex="-1">A. 区块链与密码学 <a class="header-anchor" href="#a-区块链与密码学" aria-label="Permalink to &quot;A. 区块链与密码学&quot;">​</a></h2><table tabindex="0"><thead><tr><th>术语</th><th>英文</th><th>定义</th></tr></thead><tbody><tr><td><strong>助记词</strong></td><td>Mnemonic</td><td>由 12-24 个单词组成的人类可读密钥表示，遵循 BIP39 标准</td></tr><tr><td><strong>私钥</strong></td><td>Private Key</td><td>用于签署交易的 256 位密钥，必须安全保管</td></tr><tr><td><strong>公钥</strong></td><td>Public Key</td><td>从私钥派生，用于生成地址和验证签名</td></tr><tr><td><strong>地址</strong></td><td>Address</td><td>由公钥派生的账户标识符，用于接收资产</td></tr><tr><td><strong>派生路径</strong></td><td>Derivation Path</td><td>BIP44 标准定义的密钥派生层级结构，如 <code>m/44&#39;/60&#39;/0&#39;/0/0</code></td></tr><tr><td><strong>签名</strong></td><td>Signature</td><td>使用私钥对交易或消息进行的加密证明</td></tr><tr><td><strong>哈希</strong></td><td>Hash</td><td>数据的固定长度指纹，如 SHA-256、Keccak-256</td></tr><tr><td><strong>Ed25519</strong></td><td>-</td><td>BFM 链使用的椭圆曲线签名算法</td></tr><tr><td><strong>AES-GCM</strong></td><td>-</td><td>用于加密存储的对称加密算法，提供认证加密</td></tr></tbody></table><hr><h2 id="b-资产与交易" tabindex="-1">B. 资产与交易 <a class="header-anchor" href="#b-资产与交易" aria-label="Permalink to &quot;B. 资产与交易&quot;">​</a></h2><table tabindex="0"><thead><tr><th>术语</th><th>英文</th><th>定义</th></tr></thead><tbody><tr><td><strong>原生代币</strong></td><td>Native Token</td><td>区块链的主币，如 ETH、BTC、BFM</td></tr><tr><td><strong>代币标准</strong></td><td>Token Standard</td><td>智能合约代币的接口规范，如 ERC20、TRC20</td></tr><tr><td><strong>NFT</strong></td><td>Non-Fungible Token</td><td>不可替代代币，每个代币唯一</td></tr><tr><td><strong>Gas</strong></td><td>-</td><td>交易执行所需的计算费用单位</td></tr><tr><td><strong>Gas Price</strong></td><td>-</td><td>每单位 Gas 的价格</td></tr><tr><td><strong>Gas Limit</strong></td><td>-</td><td>交易允许消耗的最大 Gas 量</td></tr><tr><td><strong>Nonce</strong></td><td>-</td><td>账户的交易序号，防止重放攻击</td></tr><tr><td><strong>UTXO</strong></td><td>Unspent Transaction Output</td><td>比特币系链的未花费输出模型</td></tr><tr><td><strong>账户模型</strong></td><td>Account Model</td><td>以太坊系链的余额状态模型</td></tr></tbody></table><hr><h2 id="c-bfm-生态特定" tabindex="-1">C. BFM 生态特定 <a class="header-anchor" href="#c-bfm-生态特定" aria-label="Permalink to &quot;C. BFM 生态特定&quot;">​</a></h2><table tabindex="0"><thead><tr><th>术语</th><th>英文</th><th>定义</th></tr></thead><tbody><tr><td><strong>BFM</strong></td><td>BioForest Meta</td><td>BFM 链的原生代币</td></tr><tr><td><strong>DWEB</strong></td><td>Decentralized Web</td><td>去中心化网页应用运行环境</td></tr><tr><td><strong>Plaoc</strong></td><td>Platform of Chains</td><td>BFM 的多链应用平台框架</td></tr><tr><td><strong>DeepLink</strong></td><td>-</td><td>用于跨应用通信的 URL 协议</td></tr><tr><td><strong>ChainConfig</strong></td><td>-</td><td>链网络的配置信息，包含 RPC、浏览器等</td></tr><tr><td><strong>订阅源</strong></td><td>Subscription URL</td><td>链配置的远程订阅地址</td></tr></tbody></table><hr><h2 id="d-架构与技术栈" tabindex="-1">D. 架构与技术栈 <a class="header-anchor" href="#d-架构与技术栈" aria-label="Permalink to &quot;D. 架构与技术栈&quot;">​</a></h2><table tabindex="0"><thead><tr><th>术语</th><th>英文</th><th>定义</th></tr></thead><tbody><tr><td><strong>Stackflow</strong></td><td>-</td><td>移动端原生体验的 React 导航框架</td></tr><tr><td><strong>Activity</strong></td><td>-</td><td>Stackflow 中的页面单元</td></tr><tr><td><strong>TanStack Query</strong></td><td>-</td><td>数据获取和缓存库，原 React Query</td></tr><tr><td><strong>TanStack Store</strong></td><td>-</td><td>轻量级响应式状态管理库</td></tr><tr><td><strong>TanStack Form</strong></td><td>-</td><td>类型安全的表单状态管理库</td></tr><tr><td><strong>shadcn/ui</strong></td><td>-</td><td>基于 Radix UI 的可复制组件库</td></tr><tr><td><strong>Zod</strong></td><td>-</td><td>TypeScript 优先的模式验证库</td></tr><tr><td><strong>Vite</strong></td><td>-</td><td>下一代前端构建工具</td></tr><tr><td><strong>Vitest</strong></td><td>-</td><td>Vite 原生的测试框架</td></tr><tr><td><strong>Playwright</strong></td><td>-</td><td>跨浏览器端到端测试框架</td></tr><tr><td><strong>Storybook</strong></td><td>-</td><td>UI 组件开发和文档工具</td></tr></tbody></table><hr><h2 id="e-服务层" tabindex="-1">E. 服务层 <a class="header-anchor" href="#e-服务层" aria-label="Permalink to &quot;E. 服务层&quot;">​</a></h2><table tabindex="0"><thead><tr><th>术语</th><th>英文</th><th>定义</th></tr></thead><tbody><tr><td><strong>Adapter</strong></td><td>适配器</td><td>实现特定链服务接口的类</td></tr><tr><td><strong>Registry</strong></td><td>注册表</td><td>管理和查找链适配器的容器</td></tr><tr><td><strong>Service</strong></td><td>服务</td><td>提供特定功能的接口模块</td></tr><tr><td><strong>Provider</strong></td><td>提供者</td><td>底层 RPC/API 客户端</td></tr><tr><td><strong>Subscribable</strong></td><td>可订阅</td><td>事件驱动的数据变更通知机制</td></tr><tr><td><strong>Branded Type</strong></td><td>品牌类型</td><td>TypeScript 中通过类型标记防止混淆的技术</td></tr></tbody></table><hr><h2 id="f-安全相关" tabindex="-1">F. 安全相关 <a class="header-anchor" href="#f-安全相关" aria-label="Permalink to &quot;F. 安全相关&quot;">​</a></h2><table tabindex="0"><thead><tr><th>术语</th><th>英文</th><th>定义</th></tr></thead><tbody><tr><td><strong>应用锁</strong></td><td>App Lock</td><td>进入应用时的身份验证机制</td></tr><tr><td><strong>生物识别</strong></td><td>Biometric</td><td>指纹或面部识别认证</td></tr><tr><td><strong>自动锁定</strong></td><td>Auto Lock</td><td>应用后台超时自动锁定</td></tr><tr><td><strong>安全存储</strong></td><td>Secure Storage</td><td>加密的本地数据存储</td></tr><tr><td><strong>盐值</strong></td><td>Salt</td><td>密码派生时添加的随机数据</td></tr><tr><td><strong>KDF</strong></td><td>Key Derivation Function</td><td>密钥派生函数，如 PBKDF2</td></tr></tbody></table><hr><h2 id="g-国际化" tabindex="-1">G. 国际化 <a class="header-anchor" href="#g-国际化" aria-label="Permalink to &quot;G. 国际化&quot;">​</a></h2><table tabindex="0"><thead><tr><th>术语</th><th>英文</th><th>定义</th></tr></thead><tbody><tr><td><strong>i18n</strong></td><td>Internationalization</td><td>国际化，使应用支持多语言</td></tr><tr><td><strong>l10n</strong></td><td>Localization</td><td>本地化，针对特定地区的适配</td></tr><tr><td><strong>RTL</strong></td><td>Right-to-Left</td><td>从右到左的文字排版方向</td></tr><tr><td><strong>LTR</strong></td><td>Left-to-Right</td><td>从左到右的文字排版方向</td></tr><tr><td><strong>Locale</strong></td><td>区域设置</td><td>语言和地区的组合标识</td></tr><tr><td><strong>Namespace</strong></td><td>命名空间</td><td>i18next 中的翻译分组</td></tr></tbody></table><hr><h2 id="h-测试相关" tabindex="-1">H. 测试相关 <a class="header-anchor" href="#h-测试相关" aria-label="Permalink to &quot;H. 测试相关&quot;">​</a></h2><table tabindex="0"><thead><tr><th>术语</th><th>英文</th><th>定义</th></tr></thead><tbody><tr><td><strong>单元测试</strong></td><td>Unit Test</td><td>测试最小功能单元的测试</td></tr><tr><td><strong>组件测试</strong></td><td>Component Test</td><td>测试 React 组件的测试</td></tr><tr><td><strong>集成测试</strong></td><td>Integration Test</td><td>测试模块间交互的测试</td></tr><tr><td><strong>E2E 测试</strong></td><td>End-to-End Test</td><td>模拟用户操作的完整流程测试</td></tr><tr><td><strong>快照测试</strong></td><td>Snapshot Test</td><td>比对 UI 输出变化的测试</td></tr><tr><td><strong>视觉回归</strong></td><td>Visual Regression</td><td>检测 UI 视觉变化的测试</td></tr><tr><td><strong>Mock</strong></td><td>模拟</td><td>替代真实依赖的测试替身</td></tr><tr><td><strong>Fixture</strong></td><td>测试夹具</td><td>预设的测试数据</td></tr></tbody></table><hr><h2 id="参考资料" tabindex="-1">参考资料 <a class="header-anchor" href="#参考资料" aria-label="Permalink to &quot;参考资料&quot;">​</a></h2><ul><li><a href="https://github.com/bitcoin/bips/blob/master/bip-0039.mediawiki" target="_blank" rel="noreferrer">BIP39 - 助记词标准</a></li><li><a href="https://github.com/bitcoin/bips/blob/master/bip-0044.mediawiki" target="_blank" rel="noreferrer">BIP44 - 派生路径标准</a></li><li><a href="https://ed25519.cr.yp.to/" target="_blank" rel="noreferrer">Ed25519 签名算法</a></li><li><a href="https://eips.ethereum.org/EIPS/eip-20" target="_blank" rel="noreferrer">ERC20 代币标准</a></li></ul>',29)])])}const u=d(e,[["render",n]]);export{c as __pageData,u as default};
