import{_ as t,c as o,o as a,ag as i}from"./chunks/framework.B0we9iV-.js";const p=JSON.parse('{"title":"01. 风格指南 (Style Guide)","description":"","frontmatter":{},"headers":[],"relativePath":"white-book/99-Documentation-Guide/01-Style-Guide.md","filePath":"white-book/99-Documentation-Guide/01-Style-Guide.md"}'),l={name:"white-book/99-Documentation-Guide/01-Style-Guide.md"};function r(c,e,n,d,s,u){return a(),o("div",null,[...e[0]||(e[0]=[i('<h1 id="_01-风格指南-style-guide" tabindex="-1">01. 风格指南 (Style Guide) <a class="header-anchor" href="#_01-风格指南-style-guide" aria-label="Permalink to &quot;01. 风格指南 (Style Guide)&quot;">​</a></h1><p>遵循 <a href="https://developers.google.com/style" target="_blank" rel="noreferrer">Google Developer Documentation Style Guide</a> 和 <a href="https://learn.microsoft.com/en-us/style-guide/welcome" target="_blank" rel="noreferrer">Microsoft Style Guide</a>。</p><h2 id="语言与语气-tone" tabindex="-1">语言与语气 (Tone) <a class="header-anchor" href="#语言与语气-tone" aria-label="Permalink to &quot;语言与语气 (Tone)&quot;">​</a></h2><ul><li><strong>专业 (Professional)</strong>: 客观、冷静，避免情绪化词汇。</li><li><strong>直接 (Direct)</strong>: 使用祈使句。例如：&quot;Create a file&quot; 而不是 &quot;You should create a file&quot;。</li><li><strong>中英混排</strong>: <ul><li>专有名词使用英文 (e.g., <code>Provider</code>, <code>Store</code>, <code>miniapp-runtime</code>)。</li><li>中英文之间保留一个空格 (e.g., &quot;配置 Webpack 插件&quot;)。</li></ul></li></ul><h2 id="格式化-formatting" tabindex="-1">格式化 (Formatting) <a class="header-anchor" href="#格式化-formatting" aria-label="Permalink to &quot;格式化 (Formatting)&quot;">​</a></h2><h3 id="标题" tabindex="-1">标题 <a class="header-anchor" href="#标题" aria-label="Permalink to &quot;标题&quot;">​</a></h3><ul><li>使用 Atx 风格 (<code>#</code>, <code>##</code>)。</li><li>H1 仅用于文件标题。</li><li>标题应包含英文对照，格式为 <code>中文 (English)</code>。</li></ul><h3 id="代码块" tabindex="-1">代码块 <a class="header-anchor" href="#代码块" aria-label="Permalink to &quot;代码块&quot;">​</a></h3><ul><li>必须指定语言 (e.g., ````typescript`)。</li><li>关键代码应包含注释。</li><li>长代码应截取核心片段，并使用 <code>...</code> 表示省略。</li></ul><h3 id="引用与提示" tabindex="-1">引用与提示 <a class="header-anchor" href="#引用与提示" aria-label="Permalink to &quot;引用与提示&quot;">​</a></h3><p>使用 GitHub Markdown 扩展语法：</p><div class="note custom-block github-alert"><p class="custom-block-title">NOTE</p><p>这是一个普通提示。</p></div><div class="warning custom-block github-alert"><p class="custom-block-title">WARNING</p><p>这是一个警告，用于强调潜在风险。</p></div><div class="tip custom-block github-alert"><p class="custom-block-title">TIP</p><p>这是一个最佳实践建议。</p></div>',14)])])}const m=t(l,[["render",r]]);export{p as __pageData,m as default};
